INSERT INTO `blog` VALUES (1, 'Angkor Wat - The largest religious monument in the World', '/backend/web/uploads/siem_reap_angkor_adventures/blog/angkor-wat-temple-in-siem-reap-cambodia_banwfp1b__F0000_20200416091749.png', '<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Angkor Wat</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Reign: King Suryavarman II</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Date:&nbsp; between 1113-1150 with later additions</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Dedicated to Vishnu &ndash; represents the Hindu Universe and Mount Meru</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Translation of Name: City Temple</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Size: 1500m x 1300m (4920 x 4625 ft) &ndash; 2 sq km.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Moat &ndash; 200 m wide</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">600m of narrative bas relief </span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">2000 Apsaras</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Angkor Wat is the largest religious monument in the world and has been in use since 1150. &nbsp;The name is modern and translates to &ldquo;City Temple&rdquo; and is apt. Angkor Wat had a city of more than 1 million inhabitants surrounding it at the height of the Khmer Empire.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Originally built as a Hindu Temple dedicated to Vishnu,&nbsp; it was converted to a Buddhist Temple by Jayavarman VII after Angkor was sacked by the Cham people in 1177.&nbsp; Unusually for Temples, Angkor Wat is oriented to the West, there are several explanations for this, the most likely being is that Vishnu is associated with the Western Cardinal Point.&nbsp; </span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">This massive stone temple actually floats, the moat ensures it doesn&rsquo;t sink.&nbsp; The water table in this part of Cambodia is close to the ground surface and the monsoon climate means the ground dries out and shrinks in the dry season as the water table lowers. The moat around the temple keeps the subsurface from drying out, holding the water table in place and preventing the temple sinking during the dry season. </span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Angkor Wat was built without using mortar, a vegetable gum was used instead. To ensure the blocks fit together seamlessly they were rubbed against each other until friction wore the surfaces down to a perfect fit. The Khmer were amazing hydro engineers; they would have impressed the Romans.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Despite all of this, the temple was never completed, and you can see the evidence of this if you look closely.&nbsp; The engraved bases of the columns aren&rsquo;t all completed and some of the bas relief shows where the sculptors intended to carve additional characters which weren&rsquo;t completed.&nbsp; </span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Angkor Wat also displays layers of military and political history in the graffiti etched into its walls. The oldest graffiti is thought to be left by the conquering Cham Army in 1177. If you look closely you will find graffiti from the French Colonial period, Japanese occupation in WW2, Khmer Rouge and Vietnamese graffiti as well as UNTAC and some modern tourists.&nbsp; Other graffiti and engravings are also carved into the walls, many of them amateur attempts to copy other symbols and carvings, leaving the observer to ponder who may have made these etchings. </span></span></p>\r\n', NULL, 1, '2020-04-16 09:17:49', 1, '2020-04-22 09:30:12', 1);
INSERT INTO `blog` VALUES (2, 'Angkor Thom - The Imperial Capital of the Khmer Empire', '/backend/web/uploads/siem_reap_angkor_adventures/blog/Angkor Thom (66)_20200416092320.jpg', '<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Angkor Thom &ndash; The Imperial Capital of King Jayavarman VII</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Reign: King Jayavarman VII</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Date:&nbsp; Late 12th Century</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Translation of Name: Capital City, or Large City</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Size: &nbsp;3km x 3km</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Moat: 100m wide</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Angkor Thom was built to be the Imperial Capital City of King Jayavarman VII, it superseded another city built by King Udayadityavarman II with the Baphoun Temple as the State Temple.&nbsp;&nbsp; Several temples and monument predate Angkor Thom were incorporated into this magnificent city.&nbsp; At its height, Angkor Thom held around 1 million inhabitants, making it the largest pre industrial city in the world. &nbsp;What was once a thriving city is now a verdant tropical jungle.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Jayavarman VII came to power after the Khmer Empire had been defeated and sacked by the Cham people. He defeated them, declared himself King and began the largest stage of construction of any Khmer King including building an invincible city to mark his victories.&nbsp; In Angkor Thom he incorporated some of the existing temples and monuments into his &ldquo;Victory Square&rdquo; where he also built his wooden palace.&nbsp;&nbsp; He built five gates into his massive city walls, including the &ldquo;Victory Gate&rdquo; and the &ldquo;Gate of the Dead&rdquo; to commemorate those who fell in battle. He also celebrated his victory in a large bas relief carved into his state temple, the Bayon which dominates the centre of his city.&nbsp; All the bridges over the moats are carved with protective symbols of the Nagas,&nbsp; Asuras and demons in the eternal &ldquo;Churning of the Sea of Milk&rdquo;.&nbsp; The warrior god Indra, the King of the celestial beings is a protector of the city, assisted by Lokeshvara, the Bodhisattva of compassion, carved into the towers over the gates watching protectively out in all directions. </span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">The city had a grand &ldquo;Victory Avenue&rdquo; leading from the Victory Gate to the Royal &ldquo;Victory Square&rdquo; where the Terrace of the Elephants and Terrace of the Leper King provided the stage for the Court to receive parades and conduct celebrations. Behind the Terraces was the Royal Palace, built of perishable materials, stone buildings were reserved for the Gods, so that today only archaeological evidence of the building remain. </span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Angkor Thom is the home of several important monuments and temples and you can find more information by clicking on the name below.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">The Bayon</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">The Terrace of the Elephants</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Terrace of the Leper King</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Phimeanakas</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Baphoun</span></span></p>\r\n', NULL, 1, '2020-04-16 09:23:20', 1, '2020-04-22 09:30:28', 1);
INSERT INTO `blog` VALUES (3, 'The Temples and Monuments of Angkor Thom', '/backend/web/uploads/siem_reap_angkor_adventures/blog/Bayon (9)_20200416092753.jpg', '<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>The Temples and Monuments of Angkor Thom</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>The Bayon</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Reign: King Jayavarman VII</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Date:&nbsp; Late 12th Century</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">The Bayon was the State Temple of Jayavarman VII, it was built in the exact center of Angkor Thom, meaning his successors had to adapt the Temple for their own uses as it was easier than demolishing and rebuilding.&nbsp; The Bayon lacks a moat and surrounding walls as those of Angkor Thom act in their place.&nbsp; The temple was originally built as a Buddhist Temple, but has been adapted to Hinduism and back again. Plus it was built over an older monument so that today, these layers of history make the center of the temple rather complex and maze like. </span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">The temple consists of massed stone towers to create a Mountain with ascending peaks.&nbsp; Today there are 37 towers and there is some debate whether the original number was 49 or 54.&nbsp; All the towers are carved with large stone faces smiling gently out over the former city.&nbsp;&nbsp; The 218 faces are most likely that of King Jayavarman VII as the incarnation of Bodhisattva Lokeshvara, the lord of compassion and benevolence. </span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">The temple is decorated with bas relief depicting &nbsp;mythological, heroic and mundane scenes.&nbsp; These carvings have answered many questions about how the temples were constructed and what everyday life was like in the 12th century Khmer Empire.&nbsp; They are fascinating and well worth lingering over, Queen Indradevi and Queen Jayarajadevi are portrayed as professors teaching groups of students. These queens were sisters and the wives of King Jayavarman VII, and also university lecturers!</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Baphoun</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Reign: King <span style=\"background-color:white\"><span style=\"color:#222222\">Udayadityavarman II</span></span>&nbsp;</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Date:&nbsp; Between 1050-1066</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Baphuon was built before Angkor Thom as a Hindu State Temple. It was so magnificent that the&nbsp;Chinese diplomat Zhou Dagun wrote at the end of the 13th Century:</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&quot;North of the Golden Tower (Bayon)... rises the Tower of Bronze... a truly astonishing spectacle,&nbsp;with more than ten chambers at its base.&quot; </span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">It was converted to become a Buddhist Temple and an enormous reclining Buddha built at the back of the Temple.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Baphuon was restored by completely dismantling it and rebuilding it. It was known as The World&#39;s Biggest Jigsaw when the plans were lost during the dark years of the 1970s.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Phimeanakas and the Royal Palace</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Reign: King Rajendravarman </span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Date:&nbsp; Between 941-968</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">The temple was built within the former Royal Palace grounds, it stands directly behind the palace site. There are no visible remains of the Palace as it was not built of stone, and stone buildings were reserved for the Gods. </span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Originally the temple pyramid was topped with a tower which was crowned with a golden pinnacle.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">The view from the top is spectacular.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>The Royal Terraces</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Terrace of the Elephants and The Terrace of the Leper King</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Reign: King Jayavarman VII</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Date:&nbsp; Late 12th Century</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">The terraces were built in front of the Royal Place and formed a platform for the King and his entourage to view parades and hold Royal Celebrations.&nbsp; They face the former Parade Ground and Victory Parade avenue and were used in a similar manner as Buckingham Palace or the Grand Parade in Washington DC are today.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Originally both terraces were covered with a pavilion, but today, only archaeological evidence of it remains as it was built of wood and other perishable materials.&nbsp; </span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><strong><span style=\"font-family:Arial,Helvetica,sans-serif\">Terrace of the Elephants</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Named after the wonderful carvings of Elephants and their Mahouts the terrace is 300m long.&nbsp; It was used as an audience hall, and according to the account of Zhou Daguan, the King appeared daily to hear the petitions and complaints of his citizens.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><strong><span style=\"font-family:Arial,Helvetica,sans-serif\">Terrace of the Leper King</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">The name comes from a statue covered in moss and lichen found here. The appearance of the statue resembled a person with leprosy and fitted in with the myth of a 9th century King who was afflicted with the disease.&nbsp; The statue has since been identified as Yama, the Lord of the Dead, and the terrace may have been used for funeral rites and as a Royal Cremation platform.</span></span></p>\r\n', NULL, 1, '2020-04-16 09:27:53', 1, '2020-04-22 09:30:49', 1);
INSERT INTO `blog` VALUES (4, 'King Jayavarman VIIâ€™s temples and monuments: Ta Prohm, Preah Khan, Neak Pean, Ta Som, Banteay Kdei', '/backend/web/uploads/siem_reap_angkor_adventures/blog/Ta Phrom (18)_20200416093044.jpg', '<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>King Jayavarman VII&rsquo;s temples and monuments: Ta Prohm, Preah Khan, Neak Pean, Ta Som, Banteay Kdei</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Ta Prohm</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Reign: King Jayavarman VII</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Date:&nbsp; Late 12th Century</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Ta Prohm was built as a monastery dedicated to King Jayavarman VII&rsquo;s mother and it was enormous. At its height had more than 80 thousand people attending to its needs and around 13 thousand of them lived within its walls, it was supported by over 3000 villages. Its original name was Rajavihara, &ldquo;The Royal Monastery&rdquo;&nbsp; and it was dedicated in 1186 with the principal deity, Prajn&aacute;paramita, &ldquo;the Perfection of Wisdom and Mother of Buddhas&rdquo; was carved in the likeness of the King&rsquo;s mother.&nbsp; </span></span></p>\r\n\r\n<p style=\"margin-right:25px\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Better known today as the &quot;Tomb Raider&quot; Temple, it is covered with strangler figs and silk-cotton trees. These trees have been deliberately left in situ and they give the temple an atmospheric, lost to the jungle feel.&nbsp;&nbsp; This decision was made to preserve the temple, removing the trees would damage too much of the temple so only those actively causing damage are removed. &nbsp;High in one of these trees is an enormous bee hive hanging from a branch. </span></span></p>\r\n\r\n<p style=\"margin-right:25px\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">A debated carving is found at Ta Prohm. Some imagine it&#39;s a dinosaur, scholars disagree saying its most likely the extinct Asian Rhinoceros with the jungle depicted behind it or it may be Agni&#39;s mount with flames behind it. Agni is a Hindu Fire God. In Khmer art, Agni has been depicted with a</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Rhinoceros as his mount. &nbsp;Stegosaurus, the dinosaur it most resembles has never been found in South East Asia.&nbsp; </span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Preah Khan</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Reign: King Jayavarman VII</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Date:&nbsp; between 1184-1191</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Preah Khan was much more than just a temple, it was a city and a university.&nbsp; It supported over 1000 teachers and in turn was supported by well over 5000 villages, almost 100 thousand people were associated with this temple.&nbsp; It was built on the site of the former palace of King Yasovarman II.&nbsp; Its stele refers to a &ldquo;lake of blood&rdquo; indicating the site was also a former battleground in the recapture of Angkor from the Cham people; the King of the Cham died here during that battle. </span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Preah Khan was dedicated to King Jayavarman&rsquo;s father and a statue of the bodhisattva Lokesvara was made in his likeness.&nbsp; The temple also contains shines to a multitude of other deities, both Buddhist and Hindu.&nbsp; </span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Today it contains the only remaining non-sacred two story structure built by the Khmer Empire, but it is believed that this building was constructed later, in the 13th century.&nbsp; The temple&rsquo;s reasonably remote location means it is often missed by tourists and it is known as a great place for bird watching. </span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Neak Pean - Jayavarman&#39;s Hospital</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Reign: Jayavarman VII</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Date: 12th Century</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Neak Pean was built by Jayavarman VII as a hospital on an island in the baray of Preah Khan. Four connected pools represent Water, Earth, Fire and Wind. Each is connected to the central water source, the main tank, by a stone conduit &quot;presided over by one of Four Great Animals namely the Elephant, Bull, Horse, and Lion&quot;, corresponding to the north, east, south, and west quarters. It was believed bathing in the water would help cure illness.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><strong>Ta Som - The little charmer</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Reign: Jayavarman VII</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Date: 12th Century</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Ta Som is a small temple built by Jayavarman VII to honour his father. It has been left largely unrestored, it is surrounded by a moat and laterite walls on three sides. This is a charming little temple which you will probably have to yourself. The trees overgrowing the temple give it a &quot;lost to the jungle&quot; feel.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><strong><span style=\"color:black\">Banteay Kdei - Citadel of the Cells</span></strong></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Reign: King Jayavarman VII</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Date: 12th Century</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"color:black\">Banteay Kdei was named for the monk&rsquo;s small, cell like dwellings and was built by Jayavarman VII probably in honour of his tutor.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"color:black\">It is a smaller temple making it easy to visit. It has been left unrestored beyond stabilisation, giving the temple an exotic and mysterious air. It is a lovely temple with beautiful photo opportunities.</span></span></span></p>\r\n', NULL, 1, '2020-04-16 09:30:44', 1, '2020-04-22 09:31:11', 1);
INSERT INTO `blog` VALUES (5, 'Temples and Monuments of the Khmer Empire', '/backend/web/uploads/siem_reap_angkor_adventures/blog/Banteay-Srey-Temple5_20200416094202.jpg', '<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">The Khmer Empire left hundreds of temples and monuments, there are 52 in the Angkor Archaeological Park alone.&nbsp; Listed chronologically below, are some of the more important temples and monuments. King Suryavarman II was another prolific builder of the 12th century, and many of the temples below were built during his reign.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Phnom Bakheng - the 1st State Temple at Angkor</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Reign: King Yasovarman I</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Date: 907</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">The &quot;Strong Hill&quot; is a natural hill and when the Capital City moved to this area in the 890s, the first</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Angkorian State Temple was built on the hill.&nbsp; The third Capital city of the Khmer Empire was built around the temple.&nbsp; The temple was abandoned around 928 but was briefly revived by Jayavarman V in 968.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Phnom Bakheng affords some wonderful sightseeing; the views over the plain are spectacular with the spires of the temples poking out of the jungle. It is especially worth visiting for sunrise and sunset.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Srah Srang - Peaceful Baray with a Tranquil Vista</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Reign: King Rejendravarman, then Jayavarman VII</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Date: Mid 10th Century, remodeled late 12th Century</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Srah Srang is a Baray, a water reservoir built around a thousand years ago to provide water &quot;for the benefit of all creatures...except for the dyke breakers&quot; (elephants).</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">It was remodelled two hundred years later and became known as Srah Srang, &quot;The Royal Bath&quot;. The</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Terrace was built at this time and is contemporary with Banteay Kdei, opposite. There was probably a small temple or sanctuary in the centre of the lake. It is perfect for sunrise or sunset.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Prasat Kravan</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Reign: King Harshavarman I, but may not have been built by Royalty</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Date: 921</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">One of the oldest temples in the Angkor Archeological Park, Prasat Kravan consists of five brick sanctuaries built in a row on a platform.&nbsp; The center sanctuary is the largest then the two flanking sanctuaries are slightly smaller and the outside two are smaller still, this was done to enhance the impression of size by means of false perspective&nbsp; and give some indication of the skill of Khmer architects of the time.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">The brickwork is extremely skillful, it does not use mortar but instead a vegetable gum which enables the fine carving of the bricks.&nbsp; It also demonstrates the Khmer mastery of firing ceramics as the bricks are of differing hardness due to the need for carving.&nbsp; When the temple was undergoing restoration in the 1960s, bricks that had to be replaced were marked with &ldquo;CA&rdquo; for Conservation Authority, enabling people to read the temple&rsquo;s history effectively.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">The brick bas relief is the only known example from the Khmer Empire and they were once brightly painted and varnished.&nbsp; They depict the various aspect of Vishnu to whom the temple is dedicated.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">After the construction of Kravan the building material began to change from solely brick to brick, sandstone and laterite and eventually sandstone and laterite alone.&nbsp; </span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Banteay Srei</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Reign: Not built by Royalty</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Date:&nbsp; 967</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">The &quot;Jewel of Khmer Art&quot; is the most exquisitely carved and beautiful of all the Khmer Empire Temples. The intricate carving in the beautiful pink sandstone brings the sculptures to life, it is a masterpiece of Khmer artistic stone-carving.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">The name is modern and translates to Citadel of the Women, but it has nothing to do with women, although the name may come from the most beautiful female deities, known as &quot;devatas&quot;, carved into the pink sandstone walls. There is a legend that female soldiers were trained here. </span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Banteay Srei was consecrated in 967, and unlike the major temples, it was not built by Royalty, but by a King&#39;s counselor. The French first saw the temple a century ago and the restoration began in the early 1930s.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">It is a relatively small temple set in a moat surrounded by woodland and nestled beside a lake. Even the location is serene and beautiful.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Banteay Samre - Restored Angkor Wat Style Temple</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Reign: King Suryavarman II completed by Yashovarman II, but may not have been built by Royalty</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Date: First half of the 12th Century</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">This Temple, the &quot;Citadel of the Samre&quot; was begun at the same time as Angkor Wat, and this temple shares the same style. The Samre was the name of the local population around Phnom Kulen, where the Khmer Empire was founded.&nbsp; The temple is also dedicated to Lord Vishnu but also contains some Buddha carvings which were defaced in the 13th Century when the Khmer Empire converted back to Hinduism.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">It has been thoroughly restored and is almost complete, indicating a little of how it may have looked when in use 900 years ago.&nbsp; The location and lack of crowds makes this temple a beautiful, peaceful place, absolutely perfect for photography. </span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Thommanon</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Reign:&nbsp; King Suryavarman II</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Date: Early 12th century</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Thommanon is built by the same King and in the same style as Angkor Wat, although it may have begun construction a little earlier.&nbsp; The temple was also dedicated to Vishnu and displays beautiful carvings, especially the <em>devatas</em> and their elegant costumes and headdresses.&nbsp; It is an elegant smaller temple which is often deserted and missed by many visitors.&nbsp; Although it flanks the Victory Parade to Angkor Thom, along with Chau Say Tevoda, they both predate Angkor Thom.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Wat Athvea</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Reign: King Suryavarman II</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Date: 12th Century</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Wat Athvea lies between Siem Reap and the Tonle Sap lake and once the Siem Reap River ran straight past it.&nbsp; The Siem Reap River was built, it was a canal used as a highway built to connect Angkor with the lake.&nbsp; It was probably the Wat built for a village with a trading point. Built around the same time as&nbsp; Angkor Wat and in the same style, this compact temple stands in the grounds of a working Wat and monastery.&nbsp;</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Beng Melea</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Reign: Probably King Suryavarman II</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Date: 12th Century</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Prasat Beng Mealea translates to &#39;Lotus Blossom Temple&#39;, it was the main temple in an important city on the ancient Royal Highway which once linked Angkor with the Empire&#39;s other cities in what is now Laos and Thailand. It has been left unrestored, and its history is a mystery, but&nbsp; because of its similarity to Angkor Wat, it is assumed to have been built in the same period and is often considered to be the &lsquo;blueprint&#39; for Angkor Wat</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">This temple is also a movie star, the walkway was built for Jean-Jacques Annaud&rsquo;s 2004 movie about two tiger cubs &#39;Two Brothers&#39; set in the Colonial period starring Guy Pearce.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">This temple is often deserted and really gives the visitor an exciting explorer vibe.</span></span></p>\r\n', NULL, 1, '2020-04-16 09:42:02', 1, '2020-04-22 09:31:42', 1);
INSERT INTO `blog` VALUES (6, 'Rolous Group - The oldest temples on the Plain of Angkor', '/backend/web/uploads/siem_reap_angkor_adventures/blog/Bakong Rolous (1)_20200416094601.jpg', '<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Rolous Group</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">These temples are the oldest surviving structures built by the Angkorian Empire in the 9th Century outside the Kulen Mountain.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">They are usually missed by most tourists so you often have the temple complex to yourself.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">This was the site of the second Capital city of the Khmer Empire and the first on the Angkorian Plain. The different styles of architecture demonstrate the change from the older Chenla Empire who built in brick to the Khmer Style of laterite and sandstone.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Rolous Group &ndash; Preah Monti</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Reign: <span style=\"background-color:white\"><span style=\"color:#626262\">King Jayavarman III or </span></span>King <span style=\"background-color:white\"><span style=\"color:#626262\">Indravarman I </span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Date: 9th Century</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">This small temple ruin was originally built in the grounds of the Royal Palace. The Palace was built of perishable materials and only archaeological traces of it remain.&nbsp; It&rsquo;s unknown when Preah Monti was built but it is the oldest of the Rolous temples, and probably the first temple built by the Khmer Empire on the plain of Angkor. It is also unknown if it was built by King Jayavarman III, about who little is known, or his successor, King Indravarman I. </span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Rolous Group - Bakong</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Reign: King Indravarman I</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Date: 881</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">This is the highlight of the Rolous Group and is the first temple mountain of its style; it features five levels, with a centre tower, and boasts an impressive height of 15 meters. It was the state temple at the centre of Hariharalaya. It collapsed at some point but was reconstructed by the French between 1937 and 1945. It also features the beautiful Bakong Pagoda, which is modern and displays interesting murals. The pagoda is in great condition and is still in use. The modern village is named after the temple.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Rolous Group - Preah Ko</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Reign: King Indravarman I</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Date: 879</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">With statues of bulls surrounding it, it&#39;s no wonder that this temple&#39;s name means &ldquo;sacred bull&rdquo;. The Temple features beautifully preserved carvings and six towers. Inside each tower is a small, active shrine. Preah Ko is the oldest temple on the plain of Angkor, it was dedicated in 879 to Shiva.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">A restoration of the towers took place in early 1990s, financed by German government.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Rolous Group - Lolei</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Reign: King Yashovarman I</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Date: 889</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Lolei was built by King Yashovarman I to honour his father, King Indravaraman I and his royal ancestors. It is the first temple to be built as an island in a baray, long since dried up. It consisted of four towers, however the original intention may have been to build six. The skill of the artisans is evident in the magnificently carved doors and their surrounds. </span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', NULL, 1, '2020-04-16 09:46:01', 1, '2020-04-22 09:32:13', 1);
INSERT INTO `blog` VALUES (7, 'The Tonle Sap - a UNESCO World Biosphere Reserve', '/backend/web/uploads/siem_reap_angkor_adventures/blog/prek-toal-bird-sanctuary-day-trip-from-siem-reap-in-krong-siem-reap-242771_20200416095104.jpg', '<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>The Tonle Sap </strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">The Tonle Sap was designated a UNESCO World Biosphere Reserve in 1997.&nbsp; It includes the Tonle Sap Lake, the most extensive freshwater lake in South-East Asia, which is located in the centre of the Tonle Sap Basin.&nbsp;The whole area consists of three different habitats: the Tonle Sap Lake in the centre, which is surrounded by floodplains and freshwater swamps, and seasonally flooded rice fields and grasslands located near two national roads that define the reserve&rsquo;s outer boundary.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">The Tonle Sap is the beating heart of Cambodia, and not just because of geography. Annually it increases in size 16 times. he lake is one of the world&rsquo;s most significant wetland ecosystems with exceptional biodiversity. The extraordinary variety of life on Tonle Sap was documented as early as the 13th century by the Chinese scholar Zhou Daguan.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">During the wet season, the Mekong River&#39;s water level rises and overflows into Tonle Sap River which, instead of draining the lake as it does during the dry season, is forced to change direction and flow back &lsquo;up&rsquo; into the lake. This is one of only two rivers in the world to flow in two directions at different times of the year.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">The lake also supports a diversity of human lifestyles as well. People live in floating villages, which move with the annual flooding, and also in stilted houses, which tower above the ground in the dry season. It is home to ethnic Vietnamese, Cham, Khmers, Christians, Buddhists and Muslims. Over a million people live on the lake. Their economy and lifestyle is deeply intertwined with the lake, the fish, the wildlife and the cycles of rising and falling waters.</span></span></p>\r\n', NULL, 1, '2020-04-16 09:51:04', 1, '2020-04-22 09:32:33', 1);
INSERT INTO `blog` VALUES (8, 'Understanding the Temples', '/backend/web/uploads/siem_reap_angkor_adventures/blog/fe_20200416095415.jpg', '<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Understanding The Temples</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">This is a short guide on general information about the temples, to help you get more out of your visit.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Unlike Churches, Mosques and Synagogues, the temples were not built as a gathering place for the faithful, rather they are the homes of the Gods built to provide protection and benevolence for the founder and family.&nbsp; They are built to reflect the universe and its center, Mount Meru, the home of the Gods.&nbsp; The moats represent rivers and oceans and the walls separate the sacred from the mundane. Often other sanctuaries were added after the temple was constructed, giving some, like Preah Khan, a rather complex layout, but the original temple layout can still be ascertained. </span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Only State Temples are built on a stepped pyramid, but even lesser prasats were built on a platform to raise the level of God above the ground. For example, Angkor Wat is a state temple, but Banteay Srei is not. </span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Construction materials</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Only temples were built with nonperishable materials, as the Gods live forever.&nbsp; The earliest temples are built of brick. This is a carryover from the method of temple construction employed by the Chenla, the immediate predecessor to the Khmer Empire. &nbsp;No mortar was used in brick temples, instead a vegetable gum was used to bond the bricks, this gave an almost invisible join and made it possible to carve the brickwork. Sometimes the brickwork was carved directly and other temples had stucco facing which was carved instead. </span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Laterite, an easy stone to work, but with an unattractive appearance was used for foundations and massive features. It is an iron rich clay that hardens when exposed to the air and sun.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Sandstone began to be used around the 10th century and eventually all temples were built of laterite and sandstone. Sandstone is wonderful material to carve, but it tends to flake eventually. </span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Religion</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Until King Jayavarman VII, the Khmer Empire was Hindu and all temples were built to honour Hindu Gods, most notably, Vishnu and Shiva.&nbsp; King Jayavarman VII (reigned 1181-1218) was a Mahayana Buddhist, and the most prolific builder of all the Khmer Kings, many of his Buddhist temples survive. However, the Empire converted back to Hinduism later and Buddhist icons were defaced.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Telling your Prasats from your Banteays</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">The names in use for the Temples are modern, and you may notice several words used repeatedly. These are the most common temple terms and their meanings.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Angkor: City</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Banteay: Citadel or fortress, a Banteay is a walled temple</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Baray: Reservoir</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Gopura: Gate</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Pagoda: Tall religious tower or building, the word is also now used for modern Wats and you may hear people saying they are going to the Pagoda.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Phnom: Mountain or hill. Even small hills are named Phnom because most of Cambodia is flat</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Prasat: Temple</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Preah: God or King and also sacred or holy</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Prei: Forest or jungle, although some Prei have been cleared</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Spean: Bridge</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Ta: Grandfather or ancestor</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Thom: Large. Angkor Thom is &#39;large city&#39;.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Wat: Temple. Angkor Wat is &#39;temple city&#39; or more accurately the &#39;Temple of the City&#39;</span></span></p>\r\n', NULL, 1, '2020-04-16 09:54:15', 1, '2020-04-22 09:32:53', 1);
INSERT INTO `blog` VALUES (9, 'Cambodian History - The Prehistory of Cambodia', '/backend/web/uploads/siem_reap_angkor_adventures/blog/Laange spean dig_20200416100514.jpg', '<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>History of Cambodia</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Prehistory of Cambodia</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Cambodia has been occupied by humans for at least 71 thousand years!&nbsp;</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">The Laang Spean cave near Battambang contained at least three distinct eras of hu&shy;man occupation. At its deepest layers, around 5 meters below the ground, flaked stone tools created around 71 thousand years ago were found. A richer middle layer contained over a hundred tools dating to be&shy;tween 11,000 and 5,000 years suggests people were living in the cave and with a hunter gatherer lifestyle.&nbsp; </span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Closer to the surface, were burial sites of four men and one woman dating from 3,700 to 3,300 years ago. Though one burial site was lavishly adorned with wild boar pendants, pottery, and stone bangles, another contained no adornment, suggesting a class system among the population.&nbsp; Unlike Laang Spean&rsquo;s prior occupants, they used the cave as a cemetery and were farmers who lived in huts on the banks of a nearby river, where mounds containing pottery fragments and stone tools that also date to this era show their habitation site.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"background-color:white\">By 4000 years ago, Cambodia was dotted with villages built inside circular earthworks. However, unlike early European settlements inside circular ramparts, these were not defensive, they were for</span> rice farming. &nbsp;<span style=\"background-color:white\">Not far from the West Baray near Prei Khmeng Temple a burial site dated to 2,000 years ago was discovered. The site contained 9 human skeletons, interestingly, one of them, a woman, had broken her femur, the large bone in the top of the leg. She was tended to by someone with sophisticated medical knowledge, the evidence showed that her bone was splinted and immobilized while it healed.&nbsp; The graves held iron tools, which would have been rare and valuable and the head of a young pig. The people were buried with bronze jewelry and beads made of glass, ceramic and semi-precious stones. &nbsp;The closest glass manufacturing was India, indicating a large trade network already in effect.&nbsp; </span></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Once there is a population of sedentary farmers who are trading, the city and state can start to develop and by 2,500 years ago, Cambodia had a kingdom known to the outside world.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">NB. &quot;Prehistoric&quot; just means &quot;without written records&quot; it does not imply a civilisation was primative&nbsp;</span></span></p>\r\n', NULL, 1, '2020-04-16 10:05:14', 1, '2020-04-22 09:33:16', 1);
INSERT INTO `blog` VALUES (10, 'Cambodian History - The Funan and Chenla Kingdoms', '/backend/web/uploads/siem_reap_angkor_adventures/blog/Chenla-troops_20200416100801.jpg', '<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><span style=\"background-color:white\">Funan</span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"background-color:white\">Little is known about &nbsp;Funan, its origins around 2,500 years ago are not yet fully established, nor is it determined whether it was one Kingdom or a collection of City States.&nbsp; It was recorded by the Chinese as a large Kingdom based in the Mekong Delta. It was huge; at its largest extent Funan covered most of SE Asia, from the Bay of Bengal to the South China Sea at central Vietnam, and south to almost the tip of the Malay Peninsula. Archaeological evidence indicated that the population of Funan, in the Cambodian region was Khmer, although it was heavily influenced by India especially in the 4th and 5th centuries. &nbsp;</span></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Funan reached the apex of its power under the 3rd century king Fan Shiman who expanded his empire&#39;s navy and improved bureaucracy that left local customs and identities largely intact, particularly in the empire&#39;s further reaches. Fan Shiman and his successors also sent ambassadors to China and India to regulate sea trade. The Funanese established a strong system of mercantilism and commercial monopolies that would become a pattern for empires in the region. </span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Funan was supplanted and absorbed in the 6th century by the&nbsp;Khmer&nbsp;Kambuja Kingdom, better known as Chenla, or Zhenla in Chinese. </span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Chenla (Kambuja)</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">The Chenla were Khmer and their name for their society was Kambuja. They first appear in the Chinese Chronicles as a Funan&#39;s vassal state which gained its independence around 550 A.D. &nbsp;&nbsp;Over the next 60 years, Chenla conquered Funan, absorbed its people and culture. The royal families of Chenla intermarried with their Funanese counterparts and generally preserved the earlier political, social, and religious institutions. Once they established control they embarked on a course of conquest that continued from the 6th until the 9th centuries. They subjugated central and upper Laos, annexed portions of the Mekong Delta, and brought what are now western Cambodia and southern Thailand under their direct control. The first capital of Chenla, named Isanapura, was established around 613 A.D. in Kompong Thom province.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">What we know about the civilisation comes from Chinese writings, the remaining Chenla temple architecture and stone inscriptions.&nbsp; The stone inscriptions are the first recorded local writing and they were made in both Sanskrit and Khmer.&nbsp; The first princes that are recorded are in a Sanskrit inscription at Stung Treng&nbsp;<span style=\"background-color:white\">province; </span>a ruler VÄ«ravarman and his father SÄrvabhauma.&nbsp; </span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">The significance of this inscription is that it indicates a change of culture under the influence of Hinduism. <span style=\"background-color:white\">Adoption of the idea of the Hindu state with its consecrated military leader, the &quot;Varman&quot; as a protector king was the basis for control and legitimacy. The Hindu religion was alluring as it offered the benefits of royal ideology with no political strings attached.&nbsp;Viravarman&rsquo;s successors continued this traditional correlation between political and religious authority. </span></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Factional disputes in the court resulted in the splitting of the kingdom in 706 into Land Chenla (Upper Chenla) and Water Chenla (Lower Chenla).&nbsp; In 715, both Chenla states were further broken up into several smaller states.&nbsp; Upper Chenla, with its capital in the Champassak province of modern Laos, was a somewhat centralized state with 30 provincial headquarters operating as administrative centers. Lower Chenla, occupying the former Funan kingdom along the Mekong Delta and the coast, had a turbulent existence with constant pirate raids from Java, Sumatra, and the Malay Peninsula. The last of the Water Chenla kings allegedly was killed around A.D. 790. A prince from Sambhpura, Java, took the reins as a puppet ruler. Ironically, the fate of Chenla and the invasion of Javanese Empire planted the actual seed for the establishment of the Angkor Empire.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">The ultimate victor in the strife that followed was the ruler of a small Khmer state located north of the Mekong Delta. His assumption of the throne as Jayavarman II (ca. A.D. 802-50) marked the beginning of a great Angkorean empire that lasted until the 15th century.</span></span></p>\r\n', NULL, 1, '2020-04-16 10:08:01', 1, '2020-04-22 09:38:23', 1);
INSERT INTO `blog` VALUES (11, 'Cambodian History - The Khmer Empire', '/backend/web/uploads/siem_reap_angkor_adventures/blog/12B49B06-66A2-4821-A619-A4CF1EBC7F9C_20200416103547.jpg', '<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>The Khmer Empire (802-1431)</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">The Khmer Empire period lasted from the early ninth century to the early fifteenth century. This was the golden age of Khmer civilization. The great temple cities of the Angkorian region are a lasting monument to the greatness of Jayavarman II and his successors.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Jayavarman II settled north of the Tonle Sap building several capitals before establishing one, Hariharalaya, near the site where the Angkorian complexes were built. &nbsp;Indravarman I (A.D. 877-89) extended Khmer control as far west as the Korat Plateau in Thailand, and his son and successor&nbsp;Yasovarman I&nbsp;(889&ndash;900), ordered the construction of huge water reservoirs (barays) north of the capital. The water management network depended on elaborate configurations of channels, ponds, and embankments and was the key to the Empire&rsquo;s prosperity for half a millennium. By freeing cultivators from dependence on unreliable seasonal monsoons, they made possible an early &quot;green revolution&quot; that provided the country with large surpluses of rice.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Suryavarman II (1113-50), one of the greatest of the Khmer Empire monarchs, expanded his kingdom&#39;s territory in a series of successful wars against the kingdom of Champa in central Vietnam, the kingdom of Nam Viet in northern Vietnam, and the small Mon states of Myanmar. He reduced to vassalage the Thai peoples and established his suzerainty over the northern part of the Malay Peninsula. His greatest achievement was the construction of Angkor Wat. Suryavarman II&#39;s reign was followed, however, by thirty years of dynastic upheaval and an invasion by the neighboring Cham, who sacked the city of Angkor in 1177.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">King Jayavarman VII (reigned 1181&ndash;1219) is generally considered to be the Khmer Empire&rsquo;s greatest King. He begins his reign by defeating the&nbsp;Champa&nbsp;in a successful campaign. Unlike his predecessors, who had adopted the cult of the Hindu god-king, Jayavarman VII was a fervent patron of Mahayana Buddhism. Casting himself as a bodhisattva, he embarked on a frenzy of building activity that included the Angkor Thom complex and the Bayon, a remarkable temple whose stone towers depict 216 faces of Buddha. Further outstanding works are attributed to him &ndash;&nbsp;Banteay Kdei,&nbsp;Ta Prohm,&nbsp;Neak Pean&nbsp;and Sra Srang. The construction of an impressive number of utilitarian and secular projects and edifices, such as maintenance of the extensive road network, in particular the royal road to&nbsp;Phimai&nbsp;and the many rest houses, bridges and hospitals. After Jayavarman VII&#39;s death in 1219, the Khmer Empire entered a long period of decline. &nbsp;Two droughts which both lasted decades interspersed with massive flooding, the change of religion, and the threat from Siam were eventually too much for the Empire. </span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">In 1353 the Siamese army captured Angkor, as well as looting the city they carried off thousands of talented and skilled Khmer back to Siam. It was recaptured by the Khmer, but wars continued and the capital was looted several times. During the same period, Khmer territory north of the present Laos border was lost to the Lao kingdom of Lan Xang. In 1431 the Siamese captured Angkor, and the Capital was moved south.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Angkor was the largest pre-industrial city in the world with a population of over 1 million and<span style=\"background-color:white\"> an elaborate infrastructure system connecting an urban sprawl of at least 1,000 square kilometers to the well-known temples at its core. </span>The combination of sophisticated wet-rice agriculture, based on an engineered irrigation system and the&nbsp;Tonle Sap&#39;s spectacular abundance in fish and aquatic fauna, guaranteed a regular food surplus. <span style=\"background-color:white\">Angkor was a megacity supporting at least 0.1% of the global population from the 11th to the </span>13th centuries. A vast workforce was employed in monument and infrastructure building and&nbsp;maintenance, which had to be supported by a larger population. </span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">There were two &lsquo;mega droughts&rsquo; that lasted decades, one in the late 1300s and another in the 1400s. Between the droughts was much larger than normal wet seasons which caused massive flooding. &nbsp;Canals silted up and the sophisticated Hydro-Engineering which supported the Empire failed. Combined with the progressive over-exploitation of the delicate local eco-system and its resources alongside large scale&nbsp;deforestation&nbsp;and the resulting&nbsp;erosion preceded the empires&#39; decline. </span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">However, Angkor Wat was never abandoned and remains a working temple.</span></span></p>\r\n', NULL, 1, '2020-04-16 10:35:47', 1, '2020-04-22 09:34:13', 1);
INSERT INTO `blog` VALUES (12, 'Cambodian History - The Middle Period and the French Colonisation', '/backend/web/uploads/siem_reap_angkor_adventures/blog/French 1900_20200416104044.jpg', '<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>The Middle Period 1431-1863</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">The Capital was moved south of the Tonle Sap, and didn&rsquo;t settle in one place for long. Longvek, although not the Capital for the longest, is one of the best known Capital cities of this era. Longvek was a port city and while never as large as Angkor, it was certainly more multicultural. Merchants and their entourages from Europe, the Middle East and all over Asia were found in the city. But, while Longvek was part of the Global trade network and was a rich city, it did not have the military might to defend it. Wars with the Siamese resulted in loss of territory and eventually the conquest of the capital Longvek in 1594. </span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Siamese and Vietnamese dominance intensified during the 17th and 18th century, resulting in frequent displacements of the seat of power as the Khmer royal authority decreased to the state of a vassal. In the early 19th century with dynasties in Vietnam and Siam firmly established, Cambodia was placed under joint suzerainty, having lost its national sovereignty.&nbsp; To save Cambodia from being incorporated into Vietnam and Siam, King&nbsp;Ang Duong&nbsp;agreed to colonial France&#39;s offers of protection.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>French colonial period (1863&ndash;1953)</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">In 1863, King&nbsp;Norodom&nbsp;signed an agreement with the French to establish a protectorate over his kingdom. The state gradually came under French colonial rule and the King remained, but was only a figurehead, he had no real power.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"background-color:white\">During World War II, the 1940&ndash;41&nbsp;Franco-Thai War&nbsp;left the&nbsp;French Indochinese&nbsp;colonial authorities in a position of weakness. The Vichy government signed an agreement with Japan to allow the Japanese military transit through French Indochina. On 9 March 1945, during the&nbsp;Japanese occupation of Cambodia, young king&nbsp;Norodom Sihanouk&nbsp;proclaimed an independent Kingdom of Kampuchea, following a formal request by the Japanese. Shortly thereafter the Japanese government nominally ratified the independence of Cambodia and established a consulate in Phnom Penh.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"background-color:white\">The&nbsp;Free French were determined to recover Indochina, though they offered Cambodia and the other Indochinese protectorates a carefully circumscribed measure of self-government. Convinced that they had a &quot;civilizing mission&quot;, they envisioned Indochina&#39;s participation in a French Union of former colonies that shared the common experience of French culture. This idea was rejected and Cambodia gained her independance in 1953.</span></span></span></p>\r\n', NULL, 1, '2020-04-16 10:40:44', 1, '2020-04-22 09:34:35', 1);
INSERT INTO `blog` VALUES (13, 'Cambodian History - The Kingdom of Cambodia and the Khmer Republic', '/backend/web/uploads/siem_reap_angkor_adventures/blog/independence-monument-phnom-penh-2ac30657-f6cb-4eae-a540-76fecc58db30_20200416105456.jpg', '<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"background-color:white\"><strong>The First Kingdom of Cambodia (1953-1970)</strong></span></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"background-color:white\">Sihanouk&#39;s &quot;royal crusade for independence&quot; resulted in grudging French acquiescence to his demands for a transfer of sovereignty. Cambodian Independence was achieved on the 9th November 1953. </span></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"background-color:white\">Neutrality was the central element of Cambodian foreign policy during the 1950s and 1960s. By the mid-1960s, parts of Cambodia&#39;s eastern provinces were serving as bases for North Vietnamese Army and&nbsp;National Liberation Front&nbsp;(NVA/NLF) forces operating against South Vietnam, and the port of&nbsp;Sihanoukville&nbsp;was being used to supply them. As NVA/VC activity grew, the United States and&nbsp;South Vietnam&nbsp;became concerned and the United States began bombing raids, contributing to destabilisation. Prince Sihanouk, fearing that the conflict between communist North Vietnam and South Vietnam might spill over to Cambodia, publicly opposed the idea of a bombing campaign by the United States along the Vietnam&ndash;Cambodia border and inside Cambodian territory. Prince Sihanouk wanted Cambodia to stay out of the North Vietnam&ndash;South Vietnam conflict and was very critical of the United States government and its allies (the South Vietnamese government). This contributed to the view that Prince Sihanouk was a North Vietnamese sympathiser and a thorn in the side of the United States.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"background-color:white\">Opposition to the government grew within the middle class and leftists including Paris-educated leaders like&nbsp;Son Sen,&nbsp;Ieng Sary, and Saloth Sar (later known as&nbsp;Pol Pot), who led an&nbsp;insurgency&nbsp;under the clandestine Communist Party of Kampuchea (CPK). Sihanouk called these insurgents the&nbsp;Khmer Rouge, literally the &quot;Red Khmer&quot;. During 1968 and 1969, the insurgency worsened. </span></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"background-color:white\"><strong>The Khmer Republic (1970-1975)</strong></span></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"background-color:white\">While visiting Beijing, Sihanouk was&nbsp;ousted by a military coup&nbsp;led by General&nbsp;Lon Nol&nbsp;and Prince Sisowath&nbsp;Sirik Matak&nbsp;on the 18th March 1970.&nbsp;&nbsp;Lon Nol assumed power after the military coup and immediately allied Cambodia with the United States. On 9 October, the Cambodian monarchy was abolished, and the country was renamed the Khmer Republic. The new regime immediately demanded that the Vietnamese communists leave Cambodia.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"background-color:white\">Hanoi rejected the new republic&#39;s request for the withdrawal of troops. In response, the United States provided assistance to the new government&#39;s armed forces, which were engaged against both Khmer Rouge insurgents and North Vietnamese forces. The North Vietnamese immediately launched armed attacks on the new government and quickly overran large parts of eastern Cambodia, reaching to within 15 miles (24&nbsp;km) of&nbsp;Phnom Penh. Then they turned the newly won territories over to the&nbsp;Khmer Rouge. </span></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"background-color:white\">In April 1970, US President Richard Nixon announced that USA and South Vietnamese ground forces had entered Cambodia in a campaign aimed at destroying NVA base areas in Cambodia.&nbsp; Although a considerable quantity of equipment was seized or destroyed by US and South Vietnamese forces, containment of North Vietnamese forces proved elusive.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"background-color:white\">The Khmer Rouge insurgency inside Cambodia continued to grow, aided by supplies and military support from North Vietnam. However, the Khmer Rouge became stronger and more independent of their Vietnamese patrons. By 1973, they were fighting battles against government forces with little or no North Vietnamese troop support, and they controlled nearly 60% of Cambodia&#39;s territory and 25% of its population. More than two million refugees from the war lived in Phnom Penh and other cities.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"background-color:white\">The US intervention and bombing campaign was a significant factor leading to increased support of the Khmer Rouge among the Cambodian peasantry. The Vietnamese intervention in Cambodia, launched at the request of the Khmer Rouge, has also been cited as a major factor in their eventual victory. &nbsp;Vietnam later admitted that it played a decisive role in their seizure of power. China armed and trained the Khmer Rouge during the civil war and continued to aid them years afterward.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n', NULL, 1, '2020-04-16 10:54:56', 1, '2020-04-22 09:34:57', 1);
INSERT INTO `blog` VALUES (14, 'Cambodian History - The Khmer Rouge', '/backend/web/uploads/siem_reap_angkor_adventures/blog/KR at Angkor Wat_20200416110041.jpg', '<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"background-color:white\"><strong>Khmer Rouge (1975-1979)</strong></span></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"background-color:white\">The Khmer Rouge took over in April 1975. Immediately after its victory, they ordered the evacuation of all cities and towns, sending the entire urban population into the countryside to work as farmers. Agriculture was collectivised, and the surviving part of the industrial base was abandoned or placed under state control. Cambodia had neither a currency nor a banking system. Religion was banned and Wats used as pigsties, ammunition dumps and barracks, many temples were vandalized and looted. </span></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"background-color:white\">20,000 people died of exhaustion or disease during the evacuation of&nbsp;Phnom Penh&nbsp;and its aftermath. Those forced to evacuate the cities were resettled in newly created villages, which lacked food, agricultural implements, and medical care. Thousands starved before the first harvest. Military and civilian leaders of the former regime who failed to disguise their pasts were executed. Some of the ethnicities in Cambodia, such as the&nbsp;Cham&nbsp;and&nbsp;Vietnamese&nbsp;suffered specific and targeted and violent persecutions. &nbsp;People were rounded up and executed for speaking a foreign language, wearing glasses, scavenging for food, absent for government assigned work, and even crying for dead loved ones. Former businessmen and bureaucrats were hunted down and killed along with their entire families; the Khmer Rouge feared that they held beliefs that could lead them to oppose their regime. A few Khmer Rouge loyalists were even killed for failing to find enough &#39;counter-revolutionaries&#39; to execute.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"background-color:white\">When Cambodian socialists began to rebel in the eastern zone of Cambodia,&nbsp;Pol Pot&nbsp;ordered his armies to exterminate 1.5 million eastern Cambodians which he branded as &quot;Cambodians with Vietnamese minds&quot; in the area.&nbsp;The purge killed more than 250,000 Cambodians in just over a month.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"background-color:white\">The Khmer Rouge relations with Vietnam and Thailand worsened rapidly as a result of border clashes. The regime broke off relations with Hanoi in December 1977, protesting Vietnam&#39;s alleged attempt to create an Indochina Federation.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"background-color:white\">In late December 1978, Vietnamese forces launched a full invasion of Cambodia, capturing Phnom Penh on 7 January 1979 and driving the remnants of Khmer Rouge army westward toward Thailand.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"background-color:white\">20,000 mass graves from the Khmer Rouge era have been found all over Cambodia. A UN investigation reported 2&ndash;3 million dead, while UNICEF estimated 3 million had been killed.&nbsp;Demographic analysis suggests that between 1.17 and 3.42 million Cambodians were killed. The death toll from war and bombing between 1965 and 1997 will probably never be known for sure. </span></span></span></p>\r\n', NULL, 1, '2020-04-16 11:00:41', 1, '2020-04-22 09:35:20', 1);
INSERT INTO `blog` VALUES (15, 'Cambodian History - The Vietnamese Occupation and UNTAC', '/backend/web/uploads/siem_reap_angkor_adventures/blog/cambodia_slide-3_460x306px_20200416110445.jpg', '<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"background-color:white\"><strong>Vietnamese Occupation and the People&rsquo;s Republic of Kampuchea (1979-1993)</strong></span></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"background-color:white\">On 10 January 1979, after the Vietnamese army invaded Cambodia and overthrew the Khmer Rouge.&nbsp; The new&nbsp;People&#39;s Republic of Kampuchea&nbsp;(PRK) was established with&nbsp;Heng Samrin&nbsp;as head of state. The Khmer Rouge forces retreated rapidly to the jungles near the Thai border. The occupation gave rise to clashes between three major resistance groups &ndash; the FUNCINPEC (a royalist party), the KPLNF (Khmer People&rsquo;s National Liberation Front) and the Khmer Rouge.&nbsp; The resulting guerrilla war displaced hundreds of thousands of Cambodians, who fled to refugee camps along the border to Thailand and tens of thousands of people were murdered throughout the country. </span></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"background-color:white\">Cambodia&rsquo;s humanitarian crisis continued as famine took hold and fighting continued. In one incident, 40 thousand Cambodian refugees were forced into a minefield on the Thai border, those who refused were shot. Over 3,000 people lost their life. Fighting erupted in the refugee camps which were also bombed by the Khmer Rouge, the Thai and the Vietnamese.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"background-color:white\">Peace efforts&nbsp;began in Paris in 1989 culminating two years later in October 1991 with the Paris Peace Agreement. The Conference gave the UN full authority to supervise a cease-fire, repatriate the displaced Khmer along the border with Thailand, disarm and demobilise the factional armies, and prepare the country for free and fair elections. The UN Advance Mission for Cambodia (UNAMIC) was deployed at the same time to maintain liaison among the factions and begin demining operations to expedite the repatriation of approximately 370,000 Cambodians from Thailand. </span></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"background-color:white\">On 16 March 1992, the&nbsp;UN Transitional Authority in Cambodia&nbsp;(UNTAC) arrived in Cambodia to begin implementation of the UN settlement plan.&nbsp;UNTAC grew into a 22,000-strong civilian and military peacekeeping force tasked to ensure the conduct of free and fair elections. </span></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"background-color:white\">Over 4 million Cambodians (about 90% of eligible voters) participated in the May 1993 elections. Pre-election violence and intimidation was widespread, according to UNTAC. The Khmer Rouge whose forces were never actually disarmed or demobilized blocked local access to polling places.</span></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"background-color:white\">Prince Ranariddh&#39;s (son of Norodom Sihanouk) royalist&nbsp;Funcinpec&nbsp;Party was the top vote recipient with 45.5% of the vote, followed by Hun Sen&#39;s&nbsp;Cambodian People&#39;s Party&nbsp;and the&nbsp;Buddhist Liberal Democratic Party, respectively. Funcinpec then entered into a coalition with the other parties that had participated in the election. A coalition government resulted between the Cambodian People&rsquo;s Party and FUNCINPEC, with two co-prime ministers &ndash;&nbsp;Hun Sen, since 1985 the prime minister in the Communist government, and&nbsp;Norodom Ranariddh.&nbsp; Hun Sen is still in power.</span></span></span></p>\r\n', NULL, 1, '2020-04-16 11:04:45', 1, '2020-04-22 09:35:40', 1);
COMMIT;

-- ----------------------------
-- Table structure for booking
-- ----------------------------
DROP TABLE IF EXISTS `booking`;
CREATE TABLE `booking` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(255) DEFAULT NULL,
  `updated_date` datetime DEFAULT NULL,
  `updated_by` int(11) DEFAULT NULL,
  `created_date` datetime DEFAULT NULL,
  `created_by` int(11) DEFAULT NULL,
  `status` int(1) DEFAULT NULL,
  `company_id` int(11) DEFAULT NULL,
  `user_id` int(11) DEFAULT NULL,
  `first_name` varchar(255) DEFAULT NULL,
  `last_name` varchar(255) DEFAULT NULL,
  `email` varchar(255) DEFAULT NULL,
  `confirm_email` varchar(255) DEFAULT NULL,
  `contact_phone` varchar(255) DEFAULT NULL,
  `country_id` int(11) DEFAULT NULL,
  `description` text DEFAULT NULL,
  `total` decimal(10,2) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Table structure for chose_us
-- ----------------------------
DROP TABLE IF EXISTS `chose_us`;
CREATE TABLE `chose_us` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) DEFAULT NULL,
  `description` text DEFAULT NULL,
  `feature_image` varchar(255) DEFAULT NULL,
  `updated_date` datetime DEFAULT NULL,
  `updated_by` int(11) DEFAULT NULL,
  `created_date` datetime DEFAULT NULL,
  `created_by` int(11) DEFAULT NULL,
  `status` int(1) DEFAULT NULL,
  `company_id` int(11) DEFAULT NULL,
  `user_id` int(11) DEFAULT NULL,
  `icon` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=10 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Table structure for city
-- ----------------------------
DROP TABLE IF EXISTS `city`;
CREATE TABLE `city` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `company_id` int(11) DEFAULT NULL,
  `country_id` int(11) DEFAULT NULL,
  `user_id` int(11) DEFAULT NULL,
  `code` varchar(100) DEFAULT NULL,
  `name` varchar(250) DEFAULT NULL,
  `description` mediumtext DEFAULT NULL,
  `feature_image` varchar(250) DEFAULT NULL,
  `video_link` varchar(250) DEFAULT NULL,
  `latitude` varchar(200) DEFAULT NULL,
  `longitude` varchar(200) DEFAULT NULL,
  `updated_by` int(11) DEFAULT NULL,
  `updated_date` datetime DEFAULT NULL,
  `status` bit(1) DEFAULT NULL,
  `created_by` int(11) DEFAULT NULL,
  `created_date` datetime DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=30 DEFAULT CHARSET=utf8;

