BEGIN;
INSERT INTO `company_profile` VALUES (1, '', 'Siem Reap Angkor Adventures', 'Street 13, Treang Village, Slor Kram Commune, Krong Siem Reap, Cambodia', 2, 2, 1, 1, '', '/dropbox/siem_reap_angkor/backend/web/uploads/eocambo_technology/company-profile/logo-2_20200325105312.png', 'Mr. Steven', 'info@eocambo.com', '', '', 'N/A', 'N/A', 'www.eocambo.com', 1, NULL, NULL, '', NULL, '<p>eOcambo Technology started in 2014 with a small team partnering with accommodation providers to help them take control of their online digital marketing and booking management using cutting edge technologies through our Hotel Link Solutions products. We have been expanding and developing rapidly especially over the past 2 years with more solutions available to accommodation providers including offering high quality custom designed websites that become a major driver for any businesses online presence. There is now a very large number of Online Travel Agents (OTA) and other online accommodation third parties where hotels and guesthouses can gain bookings, along with the massive use of Social Media by potential guests eOcambo Technology provides our clients with advice and the tools to navigate through often confusing technologies and increase their bookings and revenue. eOcambo Technology is based in Siem Reap and is now the leading provider of online digital solutions for all Cambodian accommodation providers and most importantly we have a local Khmer Support Team who are always available to offer advice and back up for our clients. Our Mission: Always create innovative solutions that meet our clients expectations. Our Vision: Be recognised as the technology company that fulfills the clients needs.</p>\r\n', '', 1, 2, 1, '2020-02-26 07:27:07', '2020-04-29 16:24:47', 1, 5, 5, NULL, '', '', '', NULL, NULL, 3, 'EOTINV-', 'EOTREC-', 'EOTEXP-', 'EOTPRO-', 'EOTQUT-', 'EOTCONT-', '163.47.15.201', 'https://www.facebook.com/eocambohospitalitygroup/', 'http://siemreap-angkor.com/', 'http://siemreap-angkor.com/', 'http://siemreap-angkor.com/');
INSERT INTO `company_profile` VALUES (2, NULL, 'eOcambo Resort & Spa', 'Siem reap', 56, 21, 1, 1, '', NULL, '', '', '', '', '', '', '', 1, NULL, NULL, '', NULL, NULL, '', 1, 3, 1, '2020-01-16 05:50:44', NULL, NULL, NULL, NULL, NULL, '', '', '', NULL, NULL, 3, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `company_profile` VALUES (3, '', '2BC Restaurant', 'Siem reap', 56, 21, 1, 1, '17252', '/dropbox/eocambo_bms_2020/backend/web/uploads/2bc_restaurant_/company-profile/69706114_2078910562417641_122259001449644032_o_20200117044806.jpg', '', '', '', '', '', '', 'eOcambo Technology', 3, NULL, '2020-01-25', '/dropbox/eocambo_bms_2020/backend/web/uploads/2bc_restaurant/company-profile/Screen Shot 2020-01-15 at 4.29.17 PM_20200120034216.png', '/Applications/XAMPP/xamppfiles/htdocs/dropbox/eocambo_bms_2020/backend/web/uploads/2bc_restaurant/company-profile/Screen Shot 2020-01-15 at 4.29.17 PM_20200120034216.png', '', '', 1, 1, 1, '2020-01-17 04:48:06', '2020-01-20 10:25:03', 1, 5, 5, NULL, '', '', '', NULL, '2020-01-21', 3, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
