-- ----------------------------
-- Records of tour_item
-- ----------------------------
BEGIN;
INSERT INTO `tour_item` VALUES (26, 4, NULL, 'Assist in the Garden', 'http://siemreap-angkor.com/backend/web/uploads/siem_reap_angkor_adventures/tour-item/ga_20200410112515.jpg', 'The farm supports people with disabilities and their families.Â  It is located in Siem Reap city. You will be helping out with activities relating to the growing of food and raising small livestock.\r\n', NULL, 4, '08:00:00', '<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Full Belly Farm</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">The farm supports people with disabilities and their families.&nbsp; It is located in Siem Reap city.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">You will be helping out with activities relating to the growing of food and raising small livestock.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Activities will depend on the time of year. You may be planting vegetable seedlings or helping to harvest produce for example.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Background</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Cambodians who are born with or acquire a disability have an especially difficult life.&nbsp; There is a social stigma attached to disabilities because Buddhism says that it is a punishment from a former life.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">The repercussions of this on some people mean shunning and being forced to move from their village. Our amazing partners help these people out in many ways, including finding a way to make a living and reagin their dignity. One of the ways they do this is with their organic, urban farm.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><em>&ldquo;Full Belly Farm&nbsp;(FBF) is</em><em>&nbsp;a small wheelchair accessible organic urban farm in Siem Reap, Cambodia. Opened in mid-2017, the farm is a place where people with disabilities receive food, shelter and training as a leg up in their fight against poverty. It is&nbsp;</em><em>a nurturing&nbsp;environment that sows the seeds of dignity, cultivates hope and harvests empowerment.&nbsp;</em><em>The farm provides residents with an opportunity to take control of their lives and to create a meaningful future for themselves and their families.&rdquo;</em></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">You can help out on Full Belly Farm and bring your children!</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Skill</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">There really are no particular skills required other than the ability to pull out a weed and have a laugh with the residents.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">The managers of Full Belly Farm have a list of things they require help with to suit all skill levels. There are even jobs for children!</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">However, if you are a gardener, you will find plenty of activities where your skills and experience will be very useful.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">This is suitable for families, and it&rsquo;s especially valuable for urban children who don&rsquo;t often get to play in a garden and learn about how food is produced.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Cost</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Volunteering at Full Belly Farm is free.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Your only expense is the transport there and back.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">It is located in Siem Reap and will take around ten minutes to get there.</span></span></p>\r\n', 1, NULL, 2, 1, '2020-04-22 10:17:29', 0, 1, '2020-04-06 14:08:30');
INSERT INTO `tour_item` VALUES (27, 4, NULL, 'Provide Sanitation', 'http://siemreap-angkor.com/backend/web/uploads/siem_reap_angkor_adventures/tour-item/san_20200410111326.jpg', 'A Toilet is taken for granted in Western countries, some of us from the west may have grown up with an outdoor outhouse, but we had one easily accessible.Â  This is not the case for too many Cambodian families.', NULL, 2, '08:00:00', '<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>BUILD A TOILET</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">A Toilet is taken for granted in Western countries, some of us from the west may have grown up with an outdoor outhouse, but we had one easily accessible.&nbsp; This is not the case for too many Cambodian families.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">In Cambodia more than 80% of the population live outside areas that have sewage systems, mostly in rural areas and UNICEF estimates that only 16% of these people have access to adequate toilets.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">A report by WaterAid found that lack of sanitation causes many long term and life-long health problems.&nbsp; Multiple bouts of diarrhea cause serious conditions like stunted growth, cognitive, emotional and social development issues which impact on a child&rsquo;s entire life.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Children who suffer multiple illnesses due to a lack of sanitation fall behind at school and end up dropping out.&nbsp; This leaves them in a cycle of poverty that very few can ever break.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">A toilet can be shared by a few families, this means that investing in one helps out many people.&nbsp; It costs less than a night on the town with dinner to help give these children a future.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>LOCATION</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">The location of the site for the toilet will usually be within 20km of Siem Reap city.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Despite the money tourism brings in, Siem Reap province is still one of the poorest in Cambodia and many rural families do not have access to basic sanitation.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">The addition of a toilet to a home provides the people with dignity and the basic human right of adequate sanitation. It is incredible the difference it can make to people&rsquo;s lives.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Expert local knowledge and care is taken to ensure that installing a toilet does not interrupt or contaminate access to clean water so at times its necessary for several families to share one toilet depending upon the surrounding land.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>SKILLS</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Volunteers do not need any specific skills as the as local people provide direction and assistance.&nbsp; Toilets installed in rural areas are relatively simple as they are non-flushing.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Most of the work needed is manual labour, digging and inserting a prefabricated concrete lining along with the covering and privacy screening.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Often a toilet is the start of bathroom, so the setup is important and controlled by the local supervisors.&nbsp;&nbsp;The experienced supervisors survey the area to ensure the toilet does not contaminate the local water supply or any wells.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">As the toilets are located in poor rural communities, working alongside the local people is a very eye opening experience for some volunteers.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Just come with a passion to help, a sense of humor and goodwill to work in a village environment where building a Toilet relies more on physical people power rather than power tools.</span></span></p>\r\n', 1, NULL, 1, 1, '2020-04-22 10:16:39', 1, 1, '2020-04-06 14:08:59');
INSERT INTO `tour_item` VALUES (28, 4, NULL, 'Provide School Facilities', 'http://siemreap-angkor.com/backend/web/uploads/siem_reap_angkor_adventures/tour-item/bn_20200410110841.jpg', 'Volunteer with us to assist a grass roots organization that helps to improveÂ  infrastructure and undertakes renovation projects in more than 20 public schools who provide education to over 8000 students in the Siem Reap area.', NULL, 5, '08:00:00', '<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Schools Improvement:</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Volunteer with us to assist a grass roots organization that helps to improve&nbsp; infrastructure and undertakes renovation projects in more than 20 public schools who provide education to over 8000 students in the Siem Reap area. All the schools included in this program are government operated in the Siem Reap province. Most are within 20km of Siem Reap city.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Background</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Perhaps not so well understood outside of Cambodia was the total elimination of the Education system during the dark years of the 1970&rsquo;s under the Khmer Rouge regime. Anyone who was a teacher or involved in any form of organized education was murdered.&nbsp; All books and education materials were destroyed by this despotic regime.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">This left Cambodia in a terrible situation after the Khmer Rouge was removed. There were no educational facilities, then the ten long years of occupation though the 1980s did not address the situation.&nbsp; Despite a marvelous effort by the Cambodian people to re-build their schools and universities, many need renovation and improvement. There just isn&rsquo;t the budget for improving or maintaining the buildings and grounds.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Requirements</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">There is a very long list of improvement projects required throughout more than 20 schools.&nbsp; Finding something that suits volunteers regardless of numbers or time available is not difficult. The organization that coordinates and supervisors the School Improvement program will provide details of projects based upon how many people can volunteer and how long they are able to help. Volunteers are then able to choose what interests them the most.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Cost</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">The project cost will be the cost of materials, transportation, and any other items required for the works to be carried out. Volunteers provide the funds required to purchase the materials necessary for their specific project. &nbsp;Basically, the budget needed to do the work.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">The organisation will request the budget around a month in advance of the project start date. They use the money to source all materials and equipment required for the project. This saves time and enable to the works to get underway as soon as the volunteers arrive. The organization can get a much better price on supplies, stretching your budget even further.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">There are two ways to approach these projects:</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Budget based. You let us know what your budget is and we arrange a project with the organisation based on that.</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Project Based. You decide what you would like to do, build, paint, lay paving etc and then we will inform you of the budget required.</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Skills</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Most projects require very little specialist skills, however there are some larger infrastructure needs where some skill or experience in the building industry would be very helpful.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">A broad example of projects may include:</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Painting</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Building</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Outfitting classrooms</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Landscaping</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Improving facilities</span></span></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n', 1, NULL, 1, 1, '2020-04-22 10:02:00', 1, 1, '2020-04-06 14:09:32');
INSERT INTO `tour_item` VALUES (29, 4, NULL, 'Provide a Home', 'http://siemreap-angkor.com/backend/web/uploads/siem_reap_angkor_adventures/tour-item/sh_20200410112241.jpg', 'Many Cambodians do not have basic housing.Â  Too many live in insecure huts built of local materials which do not survive the wet season.Â  They are not weatherproof, and allow snakes and vermin in.', NULL, 5, '07:00:00', '<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>PROVIDE A HOME</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Many Cambodians do not have basic housing.&nbsp; Too many live in insecure huts built of local materials which do not survive the wet season.&nbsp; They are not weatherproof, and allow snakes and vermin in.&nbsp; They cannot be secured, which is a big concern especially for women.&nbsp; They burn down all too easily, and all too often.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">We connect with a professional community based construction NGO to provide houses to some of the very poorest families in Siem Reap. The houses our volunteers build are wooden. They are weatherproof, can be locked up and last at least 15 years.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Volunteers will be working in rural villages, often with the family and their friends to construct the house. It is an eye-opening experience for many people to see how the majority of Cambodians live. However, a great time is&nbsp;had by our volunteers and they often fall in love with the people they work with.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Building a house for a family will give you the indescribably wonderful feeling of helping out and self-satisfaction. Volunteers often come back again and again.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>WHO GETS THE HOUSE?</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">The house can be donated to a family the volunteer may know. We have a list of people who require a house,&nbsp; otherwise the village leaders nominate the neediest families in their community. There are criteria the family need to meet before being considered for the house, this is to ensure that they cannot lose the house once it is constructed. Most houses are built for families within 25kms of Siem Reap.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>HOW LONG DOES IT TAKE TO BUILD A HOUSE?</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Housing in Cambodia is very different to in more wealthy countries but a safe and secure home is important for everyone, no matter where they live.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Each house takes around 5 days to build depending upon weather or other issues with the schedule being:</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">1 day for buying material and meeting the NGO staff, the family and village.</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">3 days to build.</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">1 day blessing ceremony and hand over to the family, and a little house warming celebration.</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Victory Dance with your new friends, lots of hugs, smiles and laughter</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Enjoy the indescribably wonderful feeling of having helped change lives for the better</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>WHAT SKILLS ARE NEEDED?</strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">A willingness to help and a sense of humour are the essential skills to work in a village environment where building relies more on physical people power rather than power tools.</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">People with construction skills are great, but anyone who can hit a nail is most welcome. The house construction is carried out by an internationally respected NGO and the volunteers work under their direction.</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">You will be working with locals, the family&rsquo;s friends and family and the NGO. It is an amazing experience to build something with the people who will benefit. It can be an eye-opening experience working in Cambodian rural villages, but everyone always has a great time. You may find that you get just as much from the experience as the family receiving the house.</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>THE HOUSE</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">The wooden houses are solid and designed to last at least 15 years and are four metres by five metres, with concrete footings, wooden walls and floorboards and a tin roof. They include one enclosed room and one room with a large swing up open window. The space underneath the house is often used as a daily living and cooking area.</span></span></p>\r\n', 1, NULL, 1, 1, '2020-04-22 10:13:59', 1, 1, '2020-04-06 14:10:11');
INSERT INTO `tour_item` VALUES (30, 4, NULL, 'Provide Water', 'http://siemreap-angkor.com/backend/web/uploads/siem_reap_angkor_adventures/tour-item/w_20200410111849.jpg', 'Our Water Well Project provides clean abundant water to poor Siem Reap communities.Â  As the essence of all life water is something that nobody can do without. ', NULL, 3, '07:00:00', '<p><span style=\"font-size:16px\"><strong>PROVIDE WATER</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Our Water Well Project provides clean abundant water to poor Siem Reap communities.&nbsp; As the essence of all life water is something that nobody can do without. UNICEF reported that 65% of rural families in Cambodia have no easy access to unpolluted water, resulting in illness and bacterial infections which can have lifelong health implications, especially for children.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Located within just a few kilometers from central Siem Reap many poor local communities do not have access to clean water. Our local NGO partner identifies needy communities in desperate need of water wells and volunteers travel into the village to help install the well. Many of the villages we work with also support people with disabilities caused by land mines.&nbsp; Those with limbs amputated face even bigger difficulties getting clean water.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Our Water Well Project changes the lives of several families at once because it is usually shared.&nbsp; A local well gives clean water for personal use and allows people to cultivate small plots of land to grow much needed extra food.&nbsp; The time saved collecting water can also be used to generate income in small cottage industries like handicrafts, sewing, making fruit chips and the like.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>SKILLS</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">The well is dug using drilling equipment, which is operated by local contractors. Then concrete is used to secure the well before a manual pump and piping is installed. It takes around 2 days to construct and fit out each well.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">No specific skills are needed as local people provide direction and assistance.&nbsp;&nbsp;However, people with construction skills are always appreciated.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Bring a passion to help and a sense of humor to work in a village where well construction relies on people power rather than power tools.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">The wells are built in rural villages, so you will be working alongside the local people.&nbsp; Expect your heart to be stolen by the children.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">It is an eye opening experience for some people. But, everyone has a great time and you are providing a basic necessity of life.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">You will experience the indescribably wonderful feeling of knowing you have helped those less fortunate than yourself.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>THE WELL</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">A well, filter and pump provides clean water for several families.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Digging and installation take 2 days.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">The depth of the well depends upon the location but it has to be dug deep enough to reach the changing water table in the dry season, usually about 30 m.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>REAL EXAMPLE</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">A well was installed for a family of four in the Djow Long Village, about 20 kms from Siem Reap.&nbsp; The well is shared by five families now, all of them had to buy water to survive.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">The well not only saved them money, but the original family is now making an extra income from it.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">They began taking in laundry, and they dug vegetable gardens and began a small plant nursery to provide edible plants for sale to the locals.&nbsp; They strung the clotheslines above the vegetable gardens and seedstock. This helps water the plants, and the flapping clothes prevent attack by insects and birds.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">The mother remarked to us about two months after the well was installed:</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><em>&ldquo;All the children are healthy now, even the animals are and all the recently born puppies survived. That has never happened before. I can keep everything clean now, and we can afford to buy our son the things he needs for school.&rdquo;</em></strong></span></span></p>\r\n', 1, NULL, 1, 1, '2020-04-22 10:12:26', 1, 1, '2020-04-06 14:10:44');
INSERT INTO `tour_item` VALUES (31, 4, NULL, 'Recycle Paper', 'http://siemreap-angkor.com/backend/web/uploads/siem_reap_angkor_adventures/tour-item/p_20200410112706.jpg', 'Cambodians who are born with or acquire a disability have an especially difficult life.Â  There is a social stigma attached to disabilities because Buddhism says that it is a punishment from a former life.', NULL, NULL, '09:00:00', '<p><span style=\"font-size:16px\">Recycle Paper</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Cambodians who are born with or acquire a disability have an especially difficult life.&nbsp; There is a social stigma attached to disabilities because Buddhism says that it is a punishment from a former life.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">The repercussions of this on some people mean shunning and being forced to move from their village. Our amazing partners help these people out in many ways, including finding a way to make a living and regain their dignity.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Handicraft manufacture is one of the methods used to give people a chance to rebuild their lives.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Here&rsquo;s how you can help, and have a fantastic time doing it.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">The organization creates wonderful products from recycled paper and they always need help producing it. You can spend a day in their workshop, learn a new skill and help those less fortunate. It really is a win win situation. Paper making is done on Sok San Road, Siem Reap, right in town.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><strong>Cost and Skill</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px\">No skill is needed to make recycled paper.&nbsp; You will be given a workshop (cost $5) to learn how to do the recycling and then that&rsquo;s all the training you need.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">This is suitable for children as well, so you can have a family day making paper and helping out disabled Cambodians.&nbsp; It&rsquo;s a great thing to do with your children, and they really enjoy it.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Papermaking is a one day activity, although you will be welcomed back if you want to help out for a longer period.</span></p>\r\n', 1, NULL, 1, 1, '2020-04-22 09:42:22', 0, 1, '2020-04-06 14:11:44');
INSERT INTO `tour_item` VALUES (34, 3, 1, 'Temple Gala Dinner - Thommanon', '/backend/web/uploads/siem_reap_angkor_adventures/tour-item/Thomannon (2)_20200424104727.jpg', 'Thommanon is architecturally similar to its slightly younger but much larger brother, Angkor Wat.Â  At night lit by candles it is stunning as the light dances with the splendidly beautiful Apsara Dancers in the niches of the temple.', 1175.50, 4, '17:00:00', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Thommanon is architecturally similar to its slightly younger but much larger brother, Angkor Wat.&nbsp; At night lit by candles it is stunning as the light dances with the splendidly beautiful Apsara Dancers in the niches of the temple.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Two traditional temple guards and two cauldrons of flame welcome you to the entrance of this spectacular scene.&nbsp; With torches lighting the way, you are escorted by a traditional band and the bare chested guards with their ivory tipped spears to a field of candles illuminating the temple beyond. Here you will be entertained by the graceful Royal Apsara Dance performed in the temple while you have pre dinner drinks.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">This visual feast is matched by a mouth-watering banquet served on beautifully decorated tables.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">This will be a night to remember.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">INCLUSIONS</span></span></strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Private air conditioned car with English speaking driver</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Transfers by private air-conditioned vehicles from and to your Siem Reap hotel</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">5 course menu, choice of Royal Khmer cuisine or western dishes</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Pre-dinner cocktails for 30 minutes with canap&eacute;s</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Free flow soft drinks, chilled juices, Angkor Beer, House red or white wine and house spirits</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Welcomed with traditionally costumed temple guards, a &quot;monkey boy&quot;, atmospheric drumming&nbsp;and traditional dance</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">5 different performances during the evening&nbsp;</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Individually printed menus with the person&#39;s name and company logo if desired</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Themed tables can be arranged</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Torch and candlelit surrounds</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Full Catering and performance teams</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Mineral water and cold towel</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">24 hours support by our team</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Portable toilet with sanitary staff on hand</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">&nbsp;What to Expect</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">You will be picked up at your hotel in the late afternoon and taken to Thommanon Temple.&nbsp; There you will be escorted to pre-dinner drinks in front of the temple and entertained until dinner is served. &nbsp;&nbsp;At the end of this enchanting evening we return you to your hotel in air conditioned comfort.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Know Before You Book</span></span></strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Round-trip transportation is available from most centrally located Siem Reap hotels. Please arrange pickup from your hotel or a nearby location when you call to confirm your reservation.</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Please be ready and waiting in your hotel lobby 15 minutes prior to your scheduled pick-up time.</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">This activity is not wheelchair accessible</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">This activity is not suitable for children</span></span></span></span></li>\r\n</ul>\r\n', 1, NULL, 2, NULL, NULL, 1, 1, '2020-04-24 10:47:27');
INSERT INTO `tour_item` VALUES (35, 3, 1, 'Gala Temple Dining - Wat Athvea', '/backend/web/uploads/siem_reap_angkor_adventures/tour-item/Wat Athvea (5)_20200424105517.jpg', 'Wat Athvea is often missed by visitors as it is south of Siem Reap on the way to the Tonle Sap.  At night its lotus tower is lit up and is absolutely beautiful.', 1175.50, 4, '17:00:00', '<p>Wat Athvea is often missed by visitors as it is south of Siem Reap on the way to the Tonle Sap. Originally the temple complex of an important trading post on the river-highway between Angkor and the Tonle Sap, it was built 900 years ago in the style of Angkor Wat. At night its lotus tower is lit up and is absolutely beautiful.</p>\r\n\r\n<p>This already lovely scene is made breathtaking by adding hundreds of candles and a gorgeously decorated al fresco dining area.&nbsp; You will be entertained by traditional performances including the Royal Apsara dance, once only performed for Kings.</p>\r\n\r\n<p>This feast for your senses is then topped by a feast for your taste buds with a meal fit of a Khmer King.</p>\r\n\r\n<p><strong>INCLUSIONS</strong></p>\r\n\r\n<ul>\r\n	<li>Private air conditioned car with English speaking driver</li>\r\n	<li>Transfers by private air-conditioned vehicles from and to your Siem Reap hotel</li>\r\n	<li>5 course menu, choice of Royal Khmer cuisine or western dishes</li>\r\n	<li>Pre-dinner cocktails for 30 minutes with canapes</li>\r\n	<li>Free flow soft drinks, chilled juices, Ankgor Beer, House red or white wine and house spirits</li>\r\n	<li>Welcomed with tradtionally costumed temple guards, &quot;monkey boy&quot;, druming&nbsp;and traditional dance</li>\r\n	<li>5 different performances during the evening&nbsp;</li>\r\n	<li>Individually printed menus with the person&#39;s name and company logo if desired</li>\r\n	<li>Themed tables can be arranged</li>\r\n	<li>Torch and candlelit surrounds</li>\r\n	<li>Full Catering and performance teams</li>\r\n	<li>Mineral water and cold towel</li>\r\n	<li>24 hours support by our team</li>\r\n	<li>Portable toilet with sanitary staff on hand</li>\r\n</ul>\r\n\r\n<p><strong>&nbsp;</strong><strong>What to Expect</strong></p>\r\n\r\n<p>You will be picked up at your hotel in the late afternoon and taken to Wat Athvea.&nbsp; There you will be escorted to pre-dinner drinks in front of the temple and entertained until dinner is served. &nbsp;&nbsp;At the end of this enchanting evening we return you to your hotel in air conditioned comfort.</p>\r\n\r\n<p><strong>Know Before You Book</strong></p>\r\n\r\n<ul>\r\n	<li>Round-trip transportation is available from most centrally located Siem Reap hotels. Please arrange pickup from your hotel or a nearby location when you call to confirm your reservation.</li>\r\n	<li>Please be ready and waiting in your hotel lobby 15 minutes prior to your scheduled pick-up time.</li>\r\n	<li>This activity is not wheelchair accessible</li>\r\n	<li>This activity is not suitable for children</li>\r\n</ul>\r\n', 1, NULL, 2, NULL, NULL, 1, 1, '2020-04-24 10:55:17');
INSERT INTO `tour_item` VALUES (36, 3, 1, 'Gala Temple Dining - Terrace of the Elephants', '/backend/web/uploads/siem_reap_angkor_adventures/tour-item/ELEPHANT TERRACE  (7)_20200424105932.jpg', 'The 900 year old Terrace of the Elephants was the ceremonial center of the Khmer Empire.Â  From here the Khmer Kings held victory parades and ceremonial events. We revive the feasting of these events in the 21stÂ century with the Gala Dinner, fit for a King of an Empire.', 1175.50, 4, '17:00:00', '<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">The 900 year old Terrace of the Elephants was the ceremonial center of the Khmer Empire.&nbsp; From here the Khmer Kings held victory parades and ceremonial events; it was their National Mall, Champs-Elysees and Buckingham Palace all rolled into one. We revive the feasting of these events in the 21st&nbsp;century with the Gala Dinner, fit for a King of an Empire and the ancient traditional performances will transport you back to the Khmer Empire at the height of its power.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">It is amazingly beautiful lit by candles and torches at night, and the atmospheric drumming adds to the ambiance.&nbsp; You will be welcomed at Baphoun Temple and escorted to pre-dinner through the candlelit temple by a traditional band.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Drinks and dinner are served to the accompaniment of traditional music and the graceful Royal Apsara dance and you will feast just as the Royal Court did millennia ago.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Jayavarman himself would have approved of the gala dinners held at this important ceremonial site.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">This is the ultimate dining experience in an ancient royal ceremonial setting.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>INCLUSIONS</strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Private air conditioned car with English speaking driver</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Transfers by private air-conditioned vehicles from and to your Siem Reap hotel</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">5 course menu, choice of Royal Khmer cuisine or western dishes</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Pre-dinner cocktails for 30 minutes with canap&eacute;s</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Free flow soft drinks, chilled juices, Angkor Beer, House red or white wine and house spirits</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Welcomed with traditionally costumed temple guards, a &quot;monkey boy&quot;, atmospheric drumming&nbsp;and traditional dance</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">5 different performances during the evening&nbsp;</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Individually printed menus with the person&#39;s name and company logo if desired</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Themed tables can be arranged</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Torch and candlelit surrounds</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Full Catering and performance teams</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Mineral water and cold towel</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">24 hours support by our team</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Portable toilet with sanitary staff on hand</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>&nbsp;</strong><strong>What to Expect</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">You will be picked up at your hotel in the late afternoon and taken to Baphoun Temple.&nbsp; There you will be escorted to pre-dinner drinks through temple to the Terrace of the Elephants and entertained until dinner is served. &nbsp;&nbsp;At the end of this enchanting evening we return you to your hotel in air conditioned comfort.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Know Before You Book</strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Round-trip transportation is available from most centrally located Siem Reap hotels. Please arrange pickup from your hotel or a nearby location when you call to confirm your reservation.</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Please be ready and waiting in your hotel lobby 15 minutes prior to your scheduled pick-up time.</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">This activity is not wheelchair accessible</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">This activity is not suitable for children</span></span></li>\r\n</ul>\r\n', 1, NULL, 2, NULL, NULL, 1, 1, '2020-04-24 10:59:32');
INSERT INTO `tour_item` VALUES (37, 3, 1, 'Gala Temple Dining - Kravan', '/backend/web/uploads/siem_reap_angkor_adventures/tour-item/Kravan (5)_20200424110426.jpg', 'Kravan is over 1100 years old and is one of the few brick temples of the Khmer Empire, possessing the only carved brick bas relief.Â  It is a stunning five tower temple that was built without using mortar.', 1175.50, 4, '05:00:00', '<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Kravan is over 1100 years old and is one of the few brick temples of the Khmer Empire, possessing the only carved brick bas relief.&nbsp; It is a stunning five tower temple that was built without using mortar.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">The temple is stunningly illuminated by candles which also dot the ground around giving the most wonderful ambiance.&nbsp; You are greeted by the atmospheric sound of drummers and escorted to pre-dinner drinks at the rear of the temple by a traditional band.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Your al fresco dining room awaits with beautifully decorated tables while the temple is lit up with a light show.&nbsp; Five difference performances will entertain you, include the Royal Apsara dance, formerly only performed by Kings.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">The visual feast for your eyes is then matched by the most delicious mouthwatering banquet leaving you with memories you will treasure.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>INCLUSIONS</strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Private air conditioned car with English speaking driver</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Transfers by private air-conditioned vehicles from and to your Siem Reap hotel</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">5 course menu, choice of Royal Khmer cuisine or western dishes</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Pre-dinner cocktails for 30 minutes with canap&eacute;s</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Free flow soft drinks, chilled juices, Angkor Beer, House red or white wine and house spirits</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Welcomed with traditionally costumed temple guards, a &quot;monkey boy&quot;, atmospheric drumming&nbsp;and traditional dance</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">5 different performances during the evening&nbsp;</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Individually printed menus with the person&#39;s name and company logo if desired</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Themed tables can be arranged</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Torch and candlelit surrounds</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Full Catering and performance teams</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Mineral water and cold towel</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">24 hours support by our team</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Portable toilet with sanitary staff on hand</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>&nbsp;What to Expect</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">You will be picked up at your hotel in the late afternoon and taken to Kravan Temple.&nbsp; There you will be escorted to pre-dinner drinks at the rear of the temple and entertained until dinner is served. &nbsp;&nbsp;At the end of this enchanting evening we return you to your hotel in air conditioned comfort.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Know Before You Book</strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Round-trip transportation is available from most centrally located Siem Reap hotels. Please arrange pickup from your hotel or a nearby location when you call to confirm your reservation.</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Please be ready and waiting in your hotel lobby 15 minutes prior to your scheduled pick-up time.</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">This activity is not wheelchair accessible</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">This activity is not suitable for children</span></span></li>\r\n</ul>\r\n', 1, NULL, 2, 1, '2020-04-28 11:06:52', 1, 1, '2020-04-24 11:04:26');
INSERT INTO `tour_item` VALUES (38, 3, 1, 'Gala Temple Dining - Banteay Samre', '/backend/web/uploads/siem_reap_angkor_adventures/tour-item/Banteay samre (2)_20200424110843.jpg', 'Banteay Samre is a 900 year old temple which once featured an internal moat that must have been stunning with the reflections of the temple upon its waters.Â  You will be greeted by atmospheric drumming taking you back to the height of the Khmer Empire, while you enjoy pre dinner drinks.', 1175.50, 4, '17:00:00', '<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Banteay Samre is a 900 year old temple which once featured an internal moat that must have been stunning with the reflections of the temple upon its waters.&nbsp; You will be greeted by atmospheric drumming taking you back to the height of the Khmer Empire, while you enjoy pre dinner drinks.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">The stunning candlelit temple then becomes the backdrop for the graceful Royal Apsara Dance formerly only performed for Kings.&nbsp; Dinner is served on beautifully decorated tables and is a meal also fit for the King of an Empire.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">This enchanted evening will leave you with a memory for life.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>INCLUSIONS</strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Private air conditioned car with English speaking driver</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Transfers by private air-conditioned vehicles from and to your Siem Reap hotel</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">5 course menu, choice of Royal Khmer cuisine or western dishes</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Pre-dinner cocktails for 30 minutes with canap&eacute;s</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Free flow soft drinks, chilled juices, Angkor Beer, House red or white wine and house spirits</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Welcomed with traditionally costumed temple guards, a &quot;monkey boy&quot;, atmospheric drumming&nbsp;and traditional dance</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">5 different performances during the evening&nbsp;</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Individually printed menus with the person&#39;s name and company logo if desired</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Themed tables can be arranged</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Torch and candlelit surrounds</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Full Catering and performance teams</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Mineral water and cold towel</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">24 hours support by our team</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Portable toilet with sanitary staff on hand</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>&nbsp;What to Expect</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">You will be picked up at your hotel in the late afternoon and taken to Banteay Samre Temple.&nbsp; There you will be escorted to pre-dinner drinks in front of the temple and entertained until dinner is served. &nbsp;&nbsp;At the end of this enchanting evening we return you to your hotel in air conditioned comfort.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Know Before You Book</strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Round-trip transportation is available from most centrally located Siem Reap hotels. Please arrange pickup from your hotel or a nearby location when you call to confirm your reservation.</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Please be ready and waiting in your hotel lobby 15 minutes prior to your scheduled pick-up time.</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">This activity is not wheelchair accessible</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">This activity is not suitable for children</span></span></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n', 1, NULL, 2, 1, '2020-04-29 09:14:17', 1, 1, '2020-04-24 11:08:43');
INSERT INTO `tour_item` VALUES (39, 3, 1, 'Khmer House Gala Dinner - Theam\'s Gallery', '/backend/web/uploads/siem_reap_angkor_adventures/tour-item/699_Theams-House-Gallery-Siem-ReapGarden-for-cocktail_20200424111210.jpg', 'Theamâ€™s Gallery House and its gardens showcase Khmer culture, art and history, there is something beautiful to discover in every nook and cranny. ', 377.00, 4, '17:00:00', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Theam&rsquo;s Gallery House and its gardens showcase Khmer culture, art and history, there is something beautiful to discover in every nook and cranny. You will be met at the entrance by a traditional Khmer band and escorted through the galleries to the stunning courtyard.&nbsp; Pre-dinner drinks are served around the sacred Bayon tree and its crop of Buddha statues illuminated by candlelight.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Dinner is served in a pavilion reached by crossing a quaint wooden bridge over a tranquil fish pond.&nbsp; The dining room is magnificently decorated with sacred and traditional Khmer patterns and mandalas on the walls and ceilings.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Dinner is served on tables beautifully decorated with white table cloths, candles and huge bouquets of flowers. This multi course feast will leave you happily sated and content.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\