INSERT INTO `tour_item` VALUES (40, 3, 1, 'Khmer House Gala Dinner - La RÃ©sidence de Krous', '/backend/web/uploads/siem_reap_angkor_adventures/tour-item/la Residence (1)_20200424111618.jpg', 'La RÃ©sidence de Krous is surrounded by lush tropical gardens and features two beautiful ponds.Â  Of an evening when it is lit by candles and torches it is absolutely stunning.', 364.00, 4, '17:00:00', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">This gorgeous contemporary wooden Khmer home is just 15 minutes from Siem Reap.&nbsp; The house is surrounded by lush tropical gardens and features two beautiful ponds.&nbsp; Of an evening when it is lit by candles and torches it is absolutely stunning.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">In this flickering light, sweetly smelling of incense, you will be entertained with traditional cultural performances including the graceful Royal Apsara dance.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Dinner is a multi-course feast, a bonanza for your taste-buds and an evening you will remember for life.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">INCLUSIONS</span></span></strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Private air conditioned car with English speaking driver</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Transfers by private air-conditioned vehicles from and to your Siem Reap hotel</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">5 course menu, choice of Royal Khmer cuisine or western dishes</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Pre-dinner cocktails for 30 minutes with canap&eacute;s</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Free flow soft drinks, chilled juices, Angkor Beer, House red or white wine and house spirits</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">5 different performances during the evening including a traditional dance on arrival&nbsp;</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Individually printed menus with the person&#39;s name and company logo if desired</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Themed tables can be arranged</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Torch and candlelit surrounds</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Full Catering and performance teams</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Venue Hire</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Mineral water and cold towel</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">24 hours support by our team</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">&nbsp;What to Expect</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">You will be picked up at your hotel in the late afternoon and taken to La R&eacute;sidence de Krous.&nbsp; There you will be escorted to pre-dinner drinks and entertained until dinner is served. &nbsp;&nbsp;At the end of this enchanting evening we return you to your hotel in air conditioned comfort.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Know Before You Book</span></span></strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Round-trip transportation is available from most centrally located Siem Reap hotels. Please arrange pickup from your hotel or a nearby location when you call to confirm your reservation.</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Please be ready and waiting in your hotel lobby 15 minutes prior to your scheduled pick-up time.</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">This activity is not wheelchair accessible</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">This activity is not suitable for children</span></span></span></span></li>\r\n</ul>\r\n', 1, NULL, 2, 1, '2020-04-28 10:10:02', 1, 1, '2020-04-24 11:16:18');
INSERT INTO `tour_item` VALUES (41, 3, 1, 'Khmer House Gala Dinner - Athakon House', '/backend/web/uploads/siem_reap_angkor_adventures/tour-item/Athakon House (12)_20200424112056.jpg', 'Athakon House is a stunning wooden house built in the classic Khmer residential style with open pavilions, fishponds and wonderful breezeways.', 403.00, 4, '05:00:00', '<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Athakon House is a stunning wooden house built in the classic Khmer residential style with open pavilions, fishponds and wonderful breezeways. It is surrounded by a flourishing tropical garden and features a sparkling swimming pool.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">The Gala Dinners dress up this classic Khmer house and garden. The candles and incense provide a sweet smelling romantic ambiance, and the light dances with statues and wood carvings of Khmer mythology and religious significance.&nbsp; During the evening you will be entertained by the Royal Apsara Dance and other cultural performances.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Dinner is a multi-course treat for your taste buds, leaving you happily sated and content.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>INCLUSIONS</strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Private air conditioned car with English speaking driver</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Transfers by private air-conditioned vehicles from and to your Siem Reap hotel</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">5 course menu, choice of Royal Khmer cuisine or western dishes</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Pre-dinner cocktails for 30 minutes with canap&eacute;s</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Free flow soft drinks, chilled juices, Angkor Beer, House red or white wine and house spirits</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">5 different performances during the evening including a traditional dance on arrival&nbsp;</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Individually printed menus with the person&#39;s name and company logo if desired</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Themed tables can be arranged</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Torch and candlelit surrounds</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Full Catering and performance teams</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Venue Hire</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Mineral water and cold towel</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">24 hours support by our team</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>&nbsp;</strong><strong>What to Expect</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">You will be picked up at your hotel in the late afternoon and taken to Athakon House.&nbsp; There you will be entertained and served an amazing multi course dinner. &nbsp;&nbsp;At the end of this enchanting evening we return you to your hotel in air conditioned comfort.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Know Before You Book</strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Round-trip transportation is available from most centrally located Siem Reap hotels. Please arrange pickup from your hotel or a nearby location when you call to confirm your reservation.</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Please be ready and waiting in your hotel lobby 15 minutes prior to your scheduled pick-up time.</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">This activity is not wheelchair accessible</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">This activity is not suitable for children</span></span></li>\r\n</ul>\r\n', 1, NULL, 2, 1, '2020-04-28 10:07:49', 1, 1, '2020-04-24 11:20:56');
INSERT INTO `tour_item` VALUES (42, 3, 1, 'Sunset Cocktails in the Ricefields', '/backend/web/uploads/siem_reap_angkor_adventures/tour-item/Srah srang (12)_20200424112702.jpg', 'Picture yourself with a delicious beverage, relaxing and watching the countryside prepare for the evening.Â  Nestled beside the Baray and surrounded by the greens and golds of the rice paddies, our Sunset cocktails are the perfect way to end your day, or begin your night.', 71.50, 3, '05:00:00', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Picture yourself with a delicious beverage, relaxing and watching the countryside prepare for the evening.&nbsp; The village of Srah Srang is ancient, it was once part of a temple complex over 800 years old and it is enchanting. Nestled beside the Baray and surrounded by the greens and golds of the rice paddies, our Sunset cocktails are the perfect way to end your day, or begin your night.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Watch the heavens change colours to gentle pinks and purples as the sky darkens into night.&nbsp; As the sun starts to dip, the birds flock in to squabble and roost among the trees, swapping places with majestic fruit bats leaving on their foraging trips for the night.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">To make this truly special, we provide cocktails and canap&eacute;s for your sophisticated enjoyment in this stunning rural setting.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">INCLUSIONS</span></span></strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Private air conditioned car with English speaking driver</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Transfers by private air-conditioned vehicles from and to your Siem Reap hotel</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">Canap&eacute;s </span></span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">Free flow of water, soft drink, Chilled Juices local beer, wine (red or white)</span></span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">Boat hire</span></span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Mineral water and cold towel</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">24 hours support by our team</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">&nbsp;Exclusions: </span></span></strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Angkor Archaeological Park ticket</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">&nbsp;What to Expect</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">You will be picked up at your hotel in the afternoon and taken to Srah Stang.&nbsp; Alternatively, you conclude a tour of the park here. At the end of this enchanting evening we return you to your hotel in air conditioned comfort.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Know Before You Book</span></span></strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Round-trip transportation is available from most centrally located Siem Reap hotels. Please arrange pickup from your hotel or a nearby location when you call to confirm your reservation.</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Please be ready and waiting in your hotel lobby 15 minutes prior to your scheduled pick-up time.</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">This activity is not wheelchair accessible</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">USD 37 for the Angkor Archaeological Park ticket can be purchased on the day if required.</span></span></span></span></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n', 1, NULL, 2, 1, '2020-04-28 10:04:41', 1, 1, '2020-04-24 11:27:02');
INSERT INTO `tour_item` VALUES (43, 3, 1, 'Sunset Cocktails on a Gondola in the Imperial Moat', '/backend/web/uploads/siem_reap_angkor_adventures/tour-item/Kong Kea Boat (5)_20200424113051.jpg', 'Finish your day floating along the moat of Angkor Thom in a traditional Khmer gondola sipping a drink and watching the sunset, just as Khmer Emperors did a thousand years ago.', 71.50, 3, '05:00:00', '<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Sunset Cocktails - Gondola on the moat of Angkor Thom</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Finish your day floating along the moat of Angkor Thom in a traditional Khmer gondola sipping a drink and watching the sunset, just as Khmer Emperors did a thousand years ago.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Relax and watch the reflections of the sky turning to jewel toned pinks and purples, as the jungle prepares for the evening. The birds coming back to squabble and roost in the trees, and the majestic fruit bats leaving on their foraging trips of the evening will fill the sky above you.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Each Gondola holds four passengers and onboard we serve soft drinks, beer and wine along with canap&eacute;s for your relaxed enjoyment.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">This has to be one of the most wonderful ways to conclude your day.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>INCLUSIONS</strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Private air conditioned car with English speaking driver</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Transfers by private air-conditioned vehicles from and to your Siem Reap hotel</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Canap&eacute;s</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Free flow of water, soft drink, Chilled Juices local beer, wine (red or white)</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Boat hire</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Mineral water and cold towel</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">24 hours support by our team</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>&nbsp;Exclusions: </strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Angkor Archaeological Park ticket</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>&nbsp;What to Expect</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">You will be picked up at your hotel in the afternoon and taken to Angkor Thom.&nbsp; Alternatively, you conclude a tour here. You will be escorted to your gondola for your drinks cruise on the moat. &nbsp;At the end of this enchanting evening we return you to your hotel in air conditioned comfort.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Know Before You Book</strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Round-trip transportation is available from most centrally located Siem Reap hotels. Please arrange pickup from your hotel or a nearby location when you call to confirm your reservation.</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Please be ready and waiting in your hotel lobby 15 minutes prior to your scheduled pick-up time.</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">This activity is not wheelchair accessible</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">USD 37 for the Angkor Archaeological Park ticket can be purchased on the day if required.</span></span></li>\r\n</ul>\r\n', 1, NULL, 2, 1, '2020-04-28 09:56:00', 1, 1, '2020-04-24 11:30:51');
INSERT INTO `tour_item` VALUES (44, 3, 1, 'Village Lunch on the Road to Banteay Srei', '/backend/web/uploads/siem_reap_angkor_adventures/tour-item/b96f70dff836f30fa89923bdf4241e51--sapa-vietnam-vietnam-travel_20200424113710.jpg', 'Banteay Srei is known as the â€œJewel of the Khmer Empireâ€ it is the most beautiful temple clad with pink sandstone.Â  ', 117.00, 3, '12:00:00', '<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Banteay Srei is known as the &ldquo;Jewel of the Khmer Empire&rdquo; it is the most beautiful temple clad with pink sandstone.&nbsp; It is about 45 minutes&rsquo; drive from Siem Reap through the beautiful Cambodian countryside and past quaint villages displaying their specialties.&nbsp; There are villages of basket weavers, charcoal burners, palm sugar makers and potters and will we stop at some of them to meet the local craftspeople.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">After visiting Banteay Srei and admiring its gorgeous intricate carvings and beautiful location, we stop at one of the villages for lunch on the return journey. The genuine warmth and friendliness of the locals will steal your heart while we fill your belly with the most delicious lunch.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">This enchanting experience will remain with you for years.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>INCLUSIONS</strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Private air conditioned car with English speaking driver</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Transfers by private air-conditioned vehicles from and to your Siem Reap hotel</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Khmer cuisine for lunch</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Free flow soft drinks, juices and Angkor Beer</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Beautifully decorated table or comfortable cushion seating</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Menu printed for each person with company logo if desired</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Mineral water and cold towel</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Full catering team</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Venue hire</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">24 hours support by our team</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>&nbsp;</strong><strong>What to Expect</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">You will be picked up at your hotel and taken to Banteay Srei Temple via the Angkor Archeological Park ticketing center if required. We stop along the way in one of the villages specializing in handicrafts and continue on to the temple. Then we stop for lunch in a village on the return journey, afterwards you are returned to your hotel.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>EXCLUSIONS</strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Angkor Archeological Park ticket</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>&nbsp;</strong><strong>Know Before You Book</strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Round-trip transportation is available from most centrally located Siem Reap hotels. Please arrange pickup from your hotel or a nearby location when you call to confirm your reservation.</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Please be ready and waiting in your hotel lobby 15 minutes prior to your scheduled pick-up time.</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">This activity is not wheelchair accessible</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">USD 37 for the Angkor Archaeological Park ticket can be purchased on the day if required.</span></span></li>\r\n</ul>\r\n', 1, NULL, 2, 1, '2020-04-28 09:51:51', 1, 1, '2020-04-24 11:37:10');
INSERT INTO `tour_item` VALUES (45, 3, 1, 'Village Lunch at Rolous', '/backend/web/uploads/siem_reap_angkor_adventures/tour-item/Bakong temple_20200424114543.jpg', 'The Rolous Village is ancient; the temples and village are more than one thousand years old and this is a wonderful opportunity to experience both ancient and modern Khmer culture.', 117.00, 3, '12:00:00', '<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">The Rolous Village is ancient; it was part of the first temple complex built on the plain of Angkor, the Rolous Group.&nbsp; The temples and village are more than one thousand years old and this is a wonderful opportunity to experience both ancient and modern Khmer culture.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Lunch is in the local ancient village where you may well make friends with the locals, giving you a glimpse into the day-to-day life and culture of many Cambodians. Their genuine warmth and friendliness will steal your heart.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">The delicious lunch is prepared and cooked on-site by an executive chef to the highest standards of hygiene.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">It is a stunning location for a scrumptious lunch where you can immerse yourself in our millennia old culture.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>INCLUSIONS</strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Private air conditioned car with English speaking driver</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Transfers by private air-conditioned vehicles from and to your Siem Reap hotel</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Khmer cuisine for lunch</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Free flow soft drinks, juices and Angkor Beer</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Beautifully decorated table or comfortable cushion seating</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Menu printed for each person with company logo if desired</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Mineral water and cold towel</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Full catering team</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Venue hire</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">24 hours support by our team</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>What to Expect</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">You will be picked up at your hotel and taken to Rolous Village via the Angkor Archaeological Park Ticket Center if required and returned after lunch. &nbsp;&nbsp;Alternatively you can break up your tour for this lunch and resume again afterwards</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Know Before You Book</strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Round-trip transportation is available from most centrally located Siem Reap hotels. Please arrange pickup from your hotel or a nearby location when you call to confirm your reservation.</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Please be ready and waiting in your hotel lobby 15 minutes prior to your scheduled pick-up time.</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">This activity is not wheelchair accessible</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">USD 37 for the Angkor Archaeological Park ticket can be purchased on the day if required.</span></span></li>\r\n</ul>\r\n', 1, NULL, 2, 1, '2020-04-28 09:40:32', 1, 1, '2020-04-24 11:45:43');
INSERT INTO `tour_item` VALUES (46, 3, 1, 'Village Lunch at Srah Srang', '/backend/web/uploads/siem_reap_angkor_adventures/tour-item/Srah srang (8)_20200424115208.jpg', 'The village of Srah Srang is ancient and was once part of the temple complex of Srah Srang and its Baray.Â  Built over a thousand years ago this was one of the most important sites of Angkor.Â  ', 117.00, 3, '12:00:00', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">The village of Srah Srang is ancient and was once part of the temple complex of Srah Srang and its Baray.&nbsp; Built over a thousand years ago this was one of the most important sites of Angkor.&nbsp;&nbsp;</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">You will dine on the most delicious lunch in the Srah Srang village and make new friends while the temple and jungle are mirrored on the tranquil waters of the baray.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">The enchanting scene and cuisine will leave you a memory to treasure for years to come.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">It is perfect for a lunch while touring the temples and you can resume your tour refreshed after lunch.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">INCLUSIONS</span></span></strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Private air conditioned car with English speaking driver</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Transfers by private air-conditioned vehicles from and to your Siem Reap hotel</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Khmer Cuisine</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Free flow soft drinks, chilled juices or Angkor Beer</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Beautifully decorated tables or comfortable cushion seating</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Full Catering team</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Venue hire</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Individual menus printed with each person&#39;s name and company logo if desired</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Mineral water and cold towel</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">24 hours support by our team</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">&nbsp;What to Expect</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">You will be picked up at your hotel and taken to Srah Srang Village via the Angkor Archaeological Park Ticket Center if required and returned after lunch. &nbsp;&nbsp;Alternatively you can break up your tour for this lunch and resume again afterwards</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Know Before You Book</span></span></strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Round-trip transportation is available from most centrally located Siem Reap hotels. Please arrange pickup from your hotel or a nearby location when you call to confirm your reservation.</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Please be ready and waiting in your hotel lobby 15 minutes prior to your scheduled pick-up time.</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">This activity is not wheelchair accessible</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">USD 37 for the Angkor Archaeological Park ticket can be purchased on the day if required.</span></span></span></span></li>\r\n</ul>\r\n', 1, NULL, 2, 1, '2020-04-28 09:31:49', 1, 1, '2020-04-24 11:52:08');
INSERT INTO `tour_item` VALUES (47, 3, 1, 'Gala Temple Lunch at Beng Mealea', '/backend/web/uploads/siem_reap_angkor_adventures/tour-item/beng-mealea_med_PB_20200428092447.jpg', 'You will dine like a colonial Governor with all the comforts they expected of a picnic, Khmer-style in the jungle, with the highest levels of modern hygiene.', 117.00, 3, '12:00:00', '<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Beng Mealea was the setting for the movie &ldquo;Two Brothers&rdquo; about twin tiger cubs in the colonial era.&nbsp; Other than the paths that were laid for the movie, the temple is largely unrestored and remains with the jungle encroaching. The 800 year old temple is about 50 kms from Siem Reap and the journey out there is through the beautiful Cambodian countryside.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">You will dine like a colonial Governor with all the comforts they expected of a picnic, Khmer-style in the jungle, with the highest levels of modern hygiene.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">This experience will fire your imagination, and titillate your senses.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>INCLUSIONS</strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Private air conditioned car with English speaking driver</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Transfers by private air-conditioned vehicles from and to your Siem Reap hotel</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Khmer Cuisine</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Free flow soft drinks, chilled juices or Angkor Beer</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Beautifully decorated tables or comfortable cushion seating</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Full Catering team</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Individual menus printed with each person&#39;s name and company logo if desired</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Mineral water and cold towel</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">24 hours support by our team</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>&nbsp;What to Expect</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">You will be picked up at your hotel for the journey Beng Mealea Temple and we will purchase the temple ticket on the way.&nbsp; There you enjoy a Khmer-style barbeque and exploration of the temple. &nbsp;&nbsp;At the end of this awesome al fresco lunch we return you to your hotel in air conditioned comfort.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>EXCLUSIONS</strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Beng Mealea entrance ticket</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>&nbsp;Know Before You Book</strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Round-trip transportation is available from most centrally located Siem Reap hotels. Please arrange pickup from your hotel or a nearby location when you call to confirm your reservation.</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Please be ready and waiting in your hotel lobby 15 minutes prior to your scheduled pick-up time.</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">This activity is not wheelchair accessible</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">USD$5 for Beng Mealea will be purchased on the day</span></span></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n', 1, NULL, 2, 1, '2020-04-28 09:28:31', 1, 1, '2020-04-24 11:57:37');
INSERT INTO `tour_item` VALUES (48, 3, 1, 'Sunrise Breakfast at Srah Srang', '/backend/web/uploads/siem_reap_angkor_adventures/tour-item/Srah srang (7)_20200424120317.jpg', 'Watch the countryside wake up and come to life as the suns peeps over the horizon to greet the new day, a feast for your eyes as you enjoy the most delicious breakfast.', 117.00, 3, '05:00:00', '<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Nestled on the shore of the Srah Srang Baray, and surrounded by the lush green and yellow of the rice fields, the ancient village is the perfect location for a sunrise breakfast.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Watch the countryside wake up and come to life as the suns peeps over the horizon to greet the new day, a feast for your eyes as you enjoy the most delicious breakfast.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Relax over your repast watching the village come to life.&nbsp; You will experience the local culture and lifestyle of the Khmer people, and their genuine warm smiles will fill your heart.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Breakfast at Srah Srang Village is perfect and a wonderful dining experience to treasure for a lifetime.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>INCLUSIONS</strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Private air conditioned car with English speaking driver</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Transfers by private air-conditioned vehicles from and to your Siem Reap hotel</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">American/Continental breakfast</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Free flow soft drinks, chilled juices and Angkor Beer</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Beautiful tables or comfortable cushions</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Full Catering team</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Menu printed with each person&#39;s name and company logo if desired</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Venue Hire</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Mineral water and cold towel</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">24 hours support by our team</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>&nbsp;Exclusions:</strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Angkor Archaeological Park ticket</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>&nbsp;What to Expect</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">You will be picked up at your hotel and taken to Srah Srang Village via the Angkor Archaeological Park Ticket Center if required and returned after breakfast. &nbsp;&nbsp;Alternatively you begin a tour with this wonderful breakfast.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Know Before You Book</strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Round-trip transportation is available from most centrally located Siem Reap hotels. Please arrange pickup from your hotel or a nearby location when you call to confirm your reservation.</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">USD 37 for the Angkor Archaeological Park ticket can be purchased on the day if required.</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Please be ready and waiting in your hotel lobby 15 minutes prior to your scheduled pick-up time.</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">This activity is not wheelchair accessible</span></span></li>\r\n</ul>\r\n', 1, NULL, 2, 1, '2020-04-28 09:21:51', 1, 1, '2020-04-24 12:03:17');
INSERT INTO `tour_item` VALUES (49, 2, 1, 'Bon Phum and Bon Dar Lin Festivals', '/backend/web/uploads/siem_reap_angkor_adventures/tour-item/Chreav Village (5)_20200427092903.jpg', 'Bon Phum Festival begins at the start of the rice harvest honors the water, land and air to ensure a good harvest.Â  It continues until Khmer New Year and culminates in a village party known as Bon Dar Lin (Festival of the Village). ', NULL, 1, '09:00:00', '<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Bon Phum Festival begins at the start of the rice harvest honors the water, land and air to ensure a good harvest.&nbsp; It continues until Khmer New Year and culminates in a village party known as Bon Dar Lin (Festival of the Village). This important festival was lost during the dark years of the late 20th&nbsp;century, but is now being revived.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Join in the reclaiming of almost lost culture and Khmer festivals in the Chreav Village with Bon Dar Lin. This memorable evening is a joyous celebration of Khmer culture with traditional games, performances and of course, lots of dancing and the most delicious banquet of Khmer cuisine.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">This is a wonderful opportunity to learn about our ancient culture and have a night of fun and feasting that you will treasure as a memory for life.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>INCLUSIONS</strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Private air conditioned car with English speaking driver</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Transfers by private air-conditioned vehicles from and to your Siem Reap hotel</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Performances and dancing</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Khmer Cuisine</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Mineral water and cold towel</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">24 hours support by our team</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>&nbsp;</strong><strong>What to Expect</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">You will be picked up at your hotel in the late afternoon and taken to Chreav Village.&nbsp; There you will have a wonderful time playing traditional games, learning to dance, watching performances and being encouraged to eat everything. &nbsp;&nbsp;At the end of this enchanting evening we return you to your hotel in air conditioned comfort.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Know Before You Book</strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Round-trip transportation is available from most centrally located Siem Reap hotels. Please arrange pickup from your hotel or a nearby location when you call to confirm your reservation.</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Please be ready and waiting in your hotel lobby 15 minutes prior to your scheduled pick-up time.</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">This activity is not wheelchair accessible</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">This activity is not suitable for children</span></span></li>\r\n</ul>\r\n', 1, NULL, 1, 1, '2020-04-28 09:17:05', 1, 1, '2020-04-27 09:29:03');
INSERT INTO `tour_item` VALUES (50, 2, 1, 'The Amazing Lives on the Tonle Sap', '/backend/web/uploads/siem_reap_angkor_adventures/tour-item/tonle-sap_20200427093416.jpg', 'Over a million people live on the Tonle Sap in both floating and stilted homes; this tour will show you how they interact with this unique and important waterway and its dramatic changes.', 45.00, 8, '09:00:00', '<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">The Tonle Sap, South &ndash;East Asia&rsquo;s largest freshwater lake, is recognised as a World Biosphere Reserve and the heartbeat of Cambodia. During the wet season the lake can increase in size by up to 16 times and it reverses the flow of the river twice a year.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Over a million people live on the Tonle Sap in both floating and stilted homes; this tour will show you how they interact with this unique and important waterway and its dramatic changes.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Along the way you will visit Kampong Phluk, a village of houses on stilts and a floating school, floating souvenir shop plus the flooded mangrove forest, depending on the season.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">In the dry season the houses of Kampong Phluk tower above the ground and in the wet, they appear to be floating. You will witness how people live in this ever changing environment where children learn to control a boat long before they learn to ride a bike. During the wet season we will take a canoe through the flooded mangrove forest.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>INCLUSIONS</strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Private car with English speaking driver</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Climate-controlled car ensures a comfortable day of exploring</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Flat-rate pricing for up to eight passengers is ideal for groups</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Tours and transfers by private air-conditioned vehicles from and to your Siem Reap hotel</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Mineral water and cold towel</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">24 hours support by our team</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>What to Expect</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">You will be picked up at your hotel to enjoy scenic views of the lake and silted houses and learn how the villagers adapt to the changing water levels.&nbsp; In the wet season we will canoe through the flooded mangrove forest. Then we return you to your hotel at the end of the tour.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Exclusions:</strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Boat Ticket</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Meals and Beverages</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Know Before You Book</strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Round-trip transportation is available from most centrally located Siem Reap hotels. Please arrange pickup from your hotel or a nearby location when you call to confirm your reservation.</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Please be ready and waiting in your hotel lobby 15 minutes prior to your scheduled pick-up time.</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">This activity is not wheelchair accessible.</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">This activity is not suitable for children under 6 years old</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Boat ticket required during the wet season.</span></span></li>\r\n</ul>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td scope=\"col\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Car with driver (4 seats)</span></span></td>\r\n			<td scope=\"col\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Maximum 3 pax</span></span></td>\r\n			<td scope=\"col\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">US$ 45 per day (8 hours max)</span></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td scope=\"col\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Van with driver (8-12 seats)</span></span></td>\r\n			<td scope=\"col\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Maximum 8 pax</span></span></td>\r\n			<td scope=\"col\"><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">US$ 65 per day (8 hours max)</span></span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', 1, NULL, 2, 1, '2020-04-27 12:36:44', 1, 1, '2020-04-27 09:34:16');
INSERT INTO `tour_item` VALUES (51, 7, 1, 'Jayavarmanâ€™s Jungle Treks â€“ The Imperial University', '/backend/web/uploads/siem_reap_angkor_adventures/tour-item/preah khan 2_20200427123127.jpg', 'Our exclusive Jayavarmanâ€™s Jungle Treks will take you through the former Imperial Capital of King Jayavarman VII (reigned 1181â€“1219), one of the Khmer Empireâ€™s greatest Kings.', 82.50, 8, '07:00:00', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">(Preah Khan, Ta Nie, Ta Prohm, Option for Angkor Wat)</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Our exclusive Jayavarman&rsquo;s Jungle Treks will take you through the former Imperial Capital of King Jayavarman VII (reigned 1181&ndash;1219), one of the Khmer Empire&rsquo;s greatest Kings. The trek follows 800 year old tracks through the jungle to hidden temples most tourists miss. Your guide is a knowledgeable local who grew up in a nearby village. He has some great stories of what he and other children used to get up to in their enchanted forest. On the trek he gives you hints for survival in the jungle, where to find food, clean water and the different uses of various plants for building shelter. A wonderful lunch of traditional Khmer Cuisine is prepared for you in one of the local villages.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">We begin in Jayavarman&rsquo;s University temple, Preah Khan which once supported almost 100 thousand people and over 5000 villages. &nbsp;Preah Khan means &lsquo;scared sword&rsquo; and the former name translates to &ldquo;Fortunate City Blessed with Victory&rdquo;.&nbsp; After Preah Khan we hike through the jungle on to Ta Nie.&nbsp; This gorgeous little temple tucked away in the jungle is seldom visited giving you a real explorer&rsquo;s vibe.&nbsp; After Ta Nie we trek to Ta Prohm, Temples with &lsquo;Ta&rsquo; in their name are often dedicated to ancestors. Ta Prohm was a huge monastery dedicated to the King&rsquo;s mother. This jungle covered temple was featured in the movie &ldquo;Tomb Raider&rdquo; and is considered one of the essential temples to visit while you are here. This tour has an optional finish at Angkor Wat.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">You have the choice of an air-conditioned vehicle or an exciting open top jeep to take you on this adventure.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">The starting time is flexible, it does not have to be at 7am, although we recommend an early start to beat the heat.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">INCLUSIONS</span></span></strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">English speaking driver and guide</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Expert local guide</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Traditional Khmer Lunch in a local village</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Transfers to and from your Siem Reap hotel</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Mineral water, fruit and cold towel</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">24 hours support by our team</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Choice of Air-conditioned vehicle or open top jeep</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Service charge &amp; government tax</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">&nbsp;What to Expect</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">The trek is an adventure, not a package tour and as such there a few things to be aware of before booking.</span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">The treks require a reasonable level of fitness</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">They are all around 10 kilometers of walking along uneven jungle trials, although the terrain is reasonably flat</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Some of the temples are very big, and have numerous very steep staircases</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Sturdy footwear is a necessity and your clothing must cover your shoulders and knees</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">You will require a valid Angkor Archaeological Park ticket</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Exclusions: </span></span></strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Angkor Archaeological Park ticket</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Travel insurance (compulsory)</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Tips &amp; gratuities</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Meals not listed</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Additional Drinks</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Know Before You Book</span></span></strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Round-trip transportation is available from most centrally located Siem Reap hotels. Please arrange pickup from your hotel or a nearby location when you call to confirm your reservation.</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Please be ready and waiting in your hotel lobby 15 minutes prior to your scheduled pick-up time.</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">This activity is not wheelchair accessible.</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">This activity is not suitable for children under 12 years old</span></span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">USD 37 for the Angkor Archaeological Park ticket can be purchased on the day if required.</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Price Per Person:</span></span></strong></span></span></p>\r\n\r\n<table cellspacing=\"0\" class=\"MsoTableGrid\" style=\"border-collapse:collapse; border:none; margin-left:31px; width:411px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"border-bottom:1px solid black; border-left:1px solid black; border-right:1px solid black; border-top:1px solid black; vertical-align:top; width:137px\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">1 Person</span></span></p>\r\n			</td>\r\n			<td style=\"border-bottom:1px solid black; border-left:none; border-right:1px solid black; border-top:1px solid black; vertical-align:top; width:137px\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">2 People</span></span></p>\r\n			</td>\r\n			<td style=\"border-bottom:1px solid black; border-left:none; border-right:1px solid black; border-top:1px solid black; vertical-align:top; width:137px\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">3-7 People</span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"border-bottom:1px solid black; border-left:1px solid black; border-right:1px solid black; border-top:none; vertical-align:top; width:137px\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">$169</span></span></p>\r\n			</td>\r\n			<td style=\"border-bottom:1px solid black; border-left:none; border-right:1px solid black; border-top:none; vertical-align:top; width:137px\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">$97.50</span></span></p>\r\n			</td>\r\n			<td style=\"border-bottom:1px solid black; border-left:none; border-right:1px solid black; border-top:none; vertical-align:top; width:137px\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">$82.50</span></span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', 1, NULL, 2, 1, '2020-04-27 12:31:27', 1, 1, '2020-04-27 09:43:17');
INSERT INTO `tour_item` VALUES (52, 7, 1, 'Jayavarmanâ€™s Jungle Treks â€“ The Imperial Temples', '/backend/web/uploads/siem_reap_angkor_adventures/tour-item/Ta Phrom (20)_20200427094845.jpg', 'Our exclusive Jayavarmanâ€™s Jungle Treks will take you through the former Imperial Capital of King Jayavarman VII (reigned 1181â€“1219), one of the Khmer Empireâ€™s greatest Kings. ', 82.50, 8, '07:00:00', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">(Ta Prohm, Angkor Thom, Bayon)</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Our exclusive Jayavarman&rsquo;s Jungle Treks will take you through the former Imperial Capital of King Jayavarman VII (reigned 1181&ndash;1219), one of the Khmer Empire&rsquo;s greatest Kings. The trek follows 800 year old tracks through the jungle to hidden temples most tourists miss. Your guide is a knowledgeable local who grew up in a nearby village. He has some great stories of what he and other children used to get up to in their enchanted forest. On the trek he gives you hints for survival in the jungle, where to find food, clean water and the different uses of various plants for building shelter. A wonderful lunch of traditional Khmer Cuisine is prepared for you in one of the local villages.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Our tour begins outside Angkor Wat and we trek through the jungle to Ta Prohm. Ta Prohm was a huge monastery dedicated to the King&rsquo;s mother. This jungle covered temple was featured in the movie &ldquo;Tomb Raider&rdquo; and is considered one of the essential temples to visit while you are here.&nbsp; We stop for lunch and then continue our trek to Jayavarman&rsquo;s former Imperial Capital City, Angkor Thom.&nbsp; Now a thriving jungle, this was once the largest city in the world.&nbsp; We enter through the &ldquo;Gate of the Dead&rdquo; to visit the former State Temple, the Bayon. This unique temple has over 200 smiling faces looking out over the former city, and bas relief depicting life here in the 12th&nbsp;century. After the Bayon we return you to your hotel.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">You have the choice of an air-conditioned vehicle or an exciting open top jeep to take you on this adventure.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">The starting time is flexible, it does not have to be at 7am, although we recommend an early start to beat the heat.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">INCLUSIONS</span></span></strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">English speaking driver and guide</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Expert local guide</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Traditional Khmer Lunch in a local village</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Transfers to and from your Siem Reap hotel</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Mineral water, fruit and cold towel</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">24 hours support by our team</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Choice of Air-conditioned vehicle or open top jeep</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Service charge &amp; government tax</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">&nbsp;What to Expect</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">The trek is an adventure, not a package tour and as such there a few things to be aware of before booking.</span></span></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">The treks require a reasonable level of fitness</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">They are all around 10 kilometers of walking along uneven jungle trials, although the terrain is reasonably flat</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Some of the temples are very big, and have numerous very steep staircases</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Sturdy footwear is a necessity and your clothing must cover your shoulders and knees</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">You will require a valid Angkor Archaeological Park ticket</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Exclusions: </span></span></strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Angkor Archaeological Park ticket</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Travel insurance (compulsory)</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Tips &amp; gratuities</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Meals not listed</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Additional Drinks</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Know Before You Book</span></span></strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Round-trip transportation is available from most centrally located Siem Reap hotels. Please arrange pickup from your hotel or a nearby location when you call to confirm your reservation.</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Please be ready and waiting in your hotel lobby 15 minutes prior to your scheduled pick-up time.</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">This activity is not wheelchair accessible.</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">This activity is not suitable for children under 12 years old</span></span></span></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">USD 37 for the Angkor Archaeological Park ticket can be purchased on the day if required.</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Price Per Person:</span></span></strong></span></span></p>\r\n\r\n<table cellspacing=\"0\" class=\"MsoTableGrid\" style=\"border-collapse:collapse; border:none; margin-left:31px; width:411px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"border-bottom:1px solid black; border-left:1px solid black; border-right:1px solid black; border-top:1px solid black; vertical-align:top; width:137px\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">1 Person</span></span></p>\r\n			</td>\r\n			<td style=\"border-bottom:1px solid black; border-left:none; border-right:1px solid black; border-top:1px solid black; vertical-align:top; width:137px\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">2 People</span></span></p>\r\n			</td>\r\n			<td style=\"border-bottom:1px solid black; border-left:none; border-right:1px solid black; border-top:1px solid black; vertical-align:top; width:137px\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">3-7 People</span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"border-bottom:1px solid black; border-left:1px solid black; border-right:1px solid black; border-top:none; vertical-align:top; width:137px\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">$169</span></span></p>\r\n			</td>\r\n			<td style=\"border-bottom:1px solid black; border-left:none; border-right:1px solid black; border-top:none; vertical-align:top; width:137px\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">$97.50</span></span></p>\r\n			</td>\r\n			<td style=\"border-bottom:1px solid black; border-left:none; border-right:1px solid black; border-top:none; vertical-align:top; width:137px\">\r\n			<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">$82.50</span></span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', 1, NULL, 2, 1, '2020-04-27 12:23:06', 1, 1, '2020-04-27 09:48:45');
INSERT INTO `tour_item` VALUES (53, 7, NULL, 'Jayavarmanâ€™s Jungle Treks â€“ The Imperial Capital', '/backend/web/uploads/siem_reap_angkor_adventures/tour-item/Bayon (9)_20200427095331.jpg', 'Our exclusive Jayavarmanâ€™s Jungle Treks will take you through the former Imperial Capital of King Jayavarman VII (reigned 1181â€“1219), one of the Khmer Empireâ€™s greatest Kings. ', 82.50, 8, '07:00:00', '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">(Angkor Thom, Bayon, Ta Nie, Ta Prohm)</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Our exclusive Jayavarman&rsquo;s Jungle Treks will take you through the former Imperial Capital of King Jayavarman VII (reigned 1181&ndash;1219), one of the Khmer Empire&rsquo;s greatest Kings. The trek follows 800 year old tracks through the jungle to hidden temples most tourists miss. Your guide is a knowledgeable local who grew up in a nearby village. He has some great stories of what he and other children used to get up to in their enchanted forest. On the trek he gives you hints for survival in the jungle, where to find food, clean water and the different uses of various plants for building shelter. A wonderful lunch of traditional Khmer Cuisine is prepared for you in one of the local villages.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">We begin in Jayavarman&rsquo;s Imperial Capital city, Angkor Thom, once the largest city in the world; it is now a thriving jungle rather than metropolis. We explore the Bayon, the former State Temple with more than 200 smiling faces and fascinating bas relief.&nbsp; Then we trek through the jungle to the Gate of the Dead and on to Ta Nie.&nbsp; This gorgeous little temple tucked away in the jungle is seldom visited giving you a real explorer&rsquo;s vibe.&nbsp; After Ta Nie we trek to Ta Prohm, Temples with &lsquo;Ta&rsquo; in their name are often dedicated to ancestors. Ta Prohm was a huge monastery dedicated to the King&rsquo;s mother. This jungle covered temple was featured in the movie &ldquo;Tomb Raider&rdquo; and is considered one of the essential temples to visit while you are here.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">You have the choice of an air-conditioned vehicle or an exciting open top jeep to take you on this adventure.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">The starting time is flexible, it does not have to be at 7am, although we recommend an early start to beat the heat.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">INCLUSIONS</span></span></strong></span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">English speaking driver and guide</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Expert local guide</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Traditional Khmer Lunch in a local village</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Transfers to and from your Siem Reap hotel</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Mineral water, fruit and cold towel</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">24 hours support by our team</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Choice of Air-conditioned vehicle or open top jeep</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Service charge &amp; government tax</span></span></span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">&nbsp;What to Expect</span></span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span st